import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [orderDetails, setOrderDetails] = useState({ items: '' });
  const [pdfBlob, setPdfBlob] = useState(null);
  const urlserver = 'http://localhost:4000';

  const generateReceipt = async () => {
    try {
      const response = await axios.post(urlserver + `/api_report_dashboard/generate-pdf`);
      const pdfBlob = new Blob([response.data], { type: 'application/pdf' });
  
      // สร้าง URL สำหรับเปิดไฟล์ PDF ในแท็บใหม่
      const pdfUrl = URL.createObjectURL(pdfBlob);
  
      // เปิดไฟล์ PDF ในแท็บใหม่
      window.open(pdfUrl, '_blank');
  
      // ตั้งค่า state เพื่อให้แสดงลิงก์ดาวน์โหลด
      setPdfBlob(pdfBlob);
    } catch (error) {
      console.error(error);
    }
  };
  

  return (
    <div>
      <h1>แอปพลิเคชันสร้างใบเสร็จ</h1>
      <div>
        <label>รายการสินค้า: </label>
        <input
          type="text"
          value={orderDetails.items}
          onChange={(e) => setOrderDetails({ items: e.target.value })}
        />
      </div>
      <button onClick={generateReceipt}>สร้างใบเสร็จ</button>
      {pdfBlob && (
        <div>
          <h2>ใบเสร็จ</h2>
          <a
            href={URL.createObjectURL(pdfBlob)}
            download="generated-pdf.pdf"
          >
            ดาวน์โหลดไฟล์ PDF
          </a>
        </div>
      )}
    </div>
  );
}

export default App;
