import React, { useState } from 'react';
import { Navigate } from 'react-router-dom';
import {
    MDBBtn,
    MDBContainer,
    MDBCard,
    MDBCardBody,
    MDBRow,
    MDBCol,
    MDBIcon,
    MDBInput
} from 'mdb-react-ui-kit';

function App() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    const handleLogin = () => {
        // ตรวจสอบเงื่อนไขการล็อกอิน ตรงนี้คุณควรมีโค้ดเพิ่มเติม
        if (email === '123' && password === '456') {
            setIsLoggedIn(true);
        } else {
            alert('Invalid email or password');
        }
    };

    if (isLoggedIn) {
        // หากล็อกอินสำเร็จแล้วให้เปลี่ยนเส้นทางไปหน้า Indexmain หรืออื่นๆ
        // และคุณควรกำหนดข้อมูลเพิ่มเติมในส่วนนี้
        // เช่น context, state, หรืออื่นๆ ที่เกี่ยวข้องกับการล็อกอิน
        return (
            // เปลี่ยนเส้นทางตามที่คุณต้องการ

            <Navigate to="/main_dashboard" />


        );
    }

    return (
        <MDBContainer className="my-5">
            <MDBCard>
                <MDBRow className='g-0'>
                    <MDBCol md='3'></MDBCol>
                    <MDBCol md='6'>
                        <MDBCardBody className='d-flex flex-column'>
                            <div className='d-flex flex-row mt-2'>
                                <MDBIcon fas icon="cubes fa-3x me-3" style={{ color: '#ff6219' }} />
                                <span className="h1 fw-bold mb-0">Logo</span>
                            </div>
                            <h5 className="fw-normal my-4 pb-3" style={{ letterSpacing: '1px' }}>Sign into your account</h5>
                            <MDBInput wrapperClass='mb-4' label='Email address' id='formControlLg' type='email' size="lg" value={email} onChange={(e) => setEmail(e.target.value)} />
                            <MDBInput wrapperClass='mb-4' label='Password' id='formControlLg' type='password' size="lg" value={password} onChange={(e) => setPassword(e.target.value)} />
                            <MDBBtn color='dark' size='lg' onClick={handleLogin}>Login</MDBBtn>
                            <a className="small text-muted" href="#!">Forgot password?</a>
                        </MDBCardBody>
                    </MDBCol>
                </MDBRow>
            </MDBCard>
        </MDBContainer>
    );
}

export default App;
