import React, { useState, useEffect } from 'react'
import { Button, Modal, Form } from 'react-bootstrap';
import Navbar from "../Navbar";
import axios from 'axios';
const Customer = () => {
    const currentDate = new Date();
    const formattedDate = currentDate.toLocaleDateString('th-TH');

    const [showModal, setShowModal] = useState(false);
    const [cus, setUsers] = useState([]);
    const [name, setName] = useState("");
    const [roomType, setRoomType] = useState("");
    const [roomNumber, setRoomNumber] = useState("");
    const [status, setStatus] = useState("");
    const [checkInDate, setCheckInDate] = useState(formattedDate);
    const urlserver = "http://localhost:4000";
    //const urlserver = "https://nodejs-ktey.onrender.com";
    useEffect(() => {
        fetchUsers();
    }, []);

    const fetchUsers = async () => {
        try {
            const response = await axios.get(urlserver + `/api_customer`);
            setUsers(response.data);
            console.log(response.data);
        } catch (error) {
            console.error(error);
        }
    };
    const ob_status = {
        1: 'พักอาศัย',
        2: 'ออก',
    };

    const openModal = () => {
        setShowModal(true);

    };
    const closeModal = () => {
        setShowModal(false);
    };
    const handleSignup = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post(urlserver + `/api_customer`, {
                name,
                roomType,
                roomNumber,
                status,
                checkInDate,
            });
            // กระบวนการอื่นๆ เมื่อสำเร็จ
            console.log(response.data); // ตัวอย่างการใช้งาน response ที่ได้จาก server
        } catch (error) {
            console.error(error);
        }
    };

    return (
        <>
            <body class="g-sidenav-show   bg-gray-100">
                <Navbar />
                <main class="main-content position-relative border-radius-lg ">
                    {/* table */}
                    <br />
                    <div align="right" class=" container-fluid ">
                        <button type="button" className="btn btn-success" onClick={openModal} >
                            เพิ่มข้อมูล
                        </button>
                    </div>
                    <div class="container-fluid py2">
                        <div class="row">


                            <div class="col-12">
                                <div class="card mb-4">


                                    <div class="card-header pb-0">
                                        <h6>ค่าเช่าที่ต้องเก็บ</h6>
                                    </div>
                                    <div class="card-body px-0 pt-0 pb-2">
                                        <div class="table-responsive p-0">
                                            <table class="table align-items-center mb-0">
                                                <thead>
                                                    <tr>
                                                        <th class="text-center  text-secondary text-xs font-weight-bolder opacity-7">ชื่อ</th>
                                                        <th class="text-center  text-secondary text-xs font-weight-bolder opacity-7 ps-2">ห้องที่/ชนิดห้อง</th>
                                                        <th class="text-center  text-secondary text-xs font-weight-bolder opacity-7">สถานะ</th>
                                                        <th class="text-center opacity-7">#</th>
                                                    </tr>
                                                </thead>
                                                <tbody>
                                                    {cus.map((file, index) => (
                                                        <tr key={index}>
                                                            <td class="text-center  text-sm font-weight-bolder opacity-8">
                                                                {file.cus_name}
                                                            </td>
                                                            <td class="text-center text-sm font-weight-bolder opacity-8">
                                                                {file.cus_room + " " + file.cus_typeroom}
                                                            </td>
                                                            <td class="text-center text-sm font-weight-bolder opacity-8">
                                                                <span class="badge badge-sm bg-gradient-success">{ob_status[file.cus_room]}</span>
                                                            </td>
                                                            <td class="text-center text-sm font-weight-bolder opacity-8"></td>
                                                        </tr>
                                                    ))}
                                                </tbody>

                                            </table>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

               
                    <Modal show={showModal} onHide={closeModal}>
                        <Modal.Header closeButton>
                            <Modal.Title>เพิ่มข้อมูล</Modal.Title>
                        </Modal.Header>
                        <Modal.Body >
                            <Form onSubmit={handleSignup}>
                                <Form.Group>
                                    <Form.Label>ชื่อ-นามสกุล</Form.Label>
                                    <Form.Control
                                        type="text"
                                        value={name}
                                        onChange={(e) => setName(e.target.value)}
                                    />
                                </Form.Group>
                                <Form.Group>
                                    <Form.Label>ชนิดของห้อง</Form.Label>
                                    <Form.Control
                                        type="text"
                                        value={roomType}
                                        onChange={(e) => setRoomType(e.target.value)}
                                    />
                                </Form.Group>
                                <Form.Group>
                                    <Form.Label>เลขห้อง</Form.Label>
                                    <Form.Control
                                        type="text"
                                        value={roomNumber}
                                        onChange={(e) => setRoomNumber(e.target.value)}
                                    />
                                </Form.Group>
                                <Form.Group>
                                    <Form.Label>สถานะ</Form.Label>
                                    <Form.Control
                                        type="text"
                                        value={status}
                                        onChange={(e) => setStatus(e.target.value)}
                                    />
                                </Form.Group>
                                <Form.Group>
                                    <Form.Label>วันที่เข้าพัก</Form.Label>
                                    <Form.Control
                                        type="text"
                                        value={checkInDate}
                                        onChange={(e) => setCheckInDate(e.target.value)}
                                    />
                                  </Form.Group>
                                <Modal.Footer>
                                    <Button variant="primary" type="submit" >
                                        ยืนยัน
                                    </Button>
                                    <Button variant="secondary" onClick={closeModal}>
                                        ปิด
                                    </Button>
                                </Modal.Footer>
                            </Form>
                        </Modal.Body>
                    </Modal>


                </main>
            </body>
        </>
    )
};

export default Customer
