import React, { useState } from 'react';
import { Button,  Form } from 'react-bootstrap';
import Swal from 'sweetalert2'; // Import Sweetalert

function App() {
  const [id_card, setid_card] = useState('');
  const [password, setpassword] = useState('');

  const handleLogin = () => {
    // ใส่เงื่อนไขการตรวจสอบรหัสผ่าน ในที่นี้ให้เปรียบเทียบกับ 'password123'
    if (password === 'password123') {
      // ถ้าล็อกอินสำเร็จ ให้ไปยังหน้า MainDashboard หรือทำส่วนที่เกี่ยวข้องต่อไป
      // ตัวอย่างนี้จะแสดง Sweetalert เพื่อแจ้งเตือน
      Swal.fire({
        icon: 'success',
        title: 'Login Successful',
        text: 'You have successfully logged in!',
        timer: 2000, // แสดง Sweetalert จนกว่าเวลาที่กำหนดจะหมด
        showConfirmButton: false,
      });
    } else {
      // ถ้ารหัสผ่านไม่ถูกต้อง แสดง Sweetalert แจ้งเตือน
      Swal.fire({
        icon: 'error',
        title: 'รหัสไม่ถูกต้อง',
        text: 'กรุณาลองใหม่อีกครั้ง',
      });
    }
  };

  return (
    <div className="container-fluid">
      <div className="row d-flex justify-content-center align-items-center h-100">
        <div className="col-12">
          <div className="card bg-white my-5 mx-auto" style={{ borderRadius: '1rem', maxWidth: '500px' }}>
            <div className="card-body p-5 w-100 d-flex flex-column">
              <h2 className="fw-bold mb-2 text-center">Login</h2>
              <p className="text-white-50 mb-3">Please enter your login and password!</p>
              <Form.Group className="mb-4 w-100" controlId="formBasicEmail">
                <Form.Label>Username/ID_Card</Form.Label>
                <Form.Control
                  type="text"

                  value={id_card}
                  onChange={(e) => setid_card(e.target.value)}
                />
              </Form.Group>
              <Form.Group className="mb-4 w-100" controlId="formBasicPassword">
                <Form.Label>Password</Form.Label>
                <Form.Control
                  type="password"
                  value={password}
                  onChange={(e) => setpassword(e.target.value)}
                />
              </Form.Group>
              <Form.Group controlId="formBasicCheckbox">
                <Form.Check type="checkbox" label="Remember password" />
              </Form.Group>
              <Button size="lg" onClick={handleLogin} style={{ backgroundColor: '#dd4b39' }}>
                Login
              </Button>
              <hr className="my-4" />
              <Button className="mb-2 w-100" size="lg" style={{ backgroundColor: '#dd4b39' }}>
                Sign in with google
              </Button>
              <Button className="mb-4 w-100" size="lg" style={{ backgroundColor: '#3b5998' }}>
                Sign in with facebook
              </Button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
