import React, { useState, useEffect } from 'react'
import { Button, Modal, Form } from 'react-bootstrap';
import Navbar from "../Navbar";
import axios from 'axios';
import Swal from 'sweetalert';
const Customer = () => {
    const currentDate = new Date();
    const formattedDate = currentDate.toISOString().split('T')[0];

    const [showModal, setShowModal] = useState(false);
    const [showModaledit, setShowModaledit] = useState(false);

    const [users, setUsers] = useState([]);
    const [room_id, setRrom_id] = useState();
    const [room_typename, setRoom_typename] = useState("");
    const [room_name, seteRoom_name] = useState("");


    const [edit_room_id, set_edit_Room_id] = useState();
    const [edit_cus_name, set_edit_Cus_name] = useState("");
    const [edit_cus_typeroom, set_edit_Cus_typeroom] = useState("");
    const [edit_cus_room, set_edit_Cus_room] = useState("");
    const [edit_cus_status, set_edit_Cus_status] = useState("");
    const [edit_cus_datein, set_edit_Cus_datein] = useState("");

    const [currentPage, setCurrentPage] = useState(1);
    const [totalPages, setTotalPages] = useState(1);
    const urlserver = "http://localhost:4000";
    //const urlserver = "https://nodejs-ktey.onrender.com";
    useEffect(() => {
        fetchUsers();

    }, []);


    const startIndex = (currentPage - 1) * 10;
    const endIndex = currentPage * 10;
    const currentUsers = users.slice(startIndex, endIndex);
    const changePage = (page) => {
        setCurrentPage(page);
    };
    const fetchUsers = async () => {
        try {
            const response = await axios.get(urlserver + `/api_customer`);
            setUsers(response.data);
            const num = response.data.length
            if (num > 0) {
                const room_id_numlast = response.data[num - 1].room_id
                setroom_id(room_id_numlast + 1);
            } else {
                setroom_id(1);
            }
            setTotalPages(Math.ceil(response.data.length / 10));
            const currentDate = new Date();
            const formattedDate = currentDate.toISOString().split('T')[0];
 
            setCus_name("");
            setCus_typeroom("");
            setCus_room("");
            setCus_status(1);
            setCus_datein(formattedDate);
        } catch (error) {
            console.error(error);
        }
    };
    const fetchedit = async (id) => {
        try {
            const response = await axios.get(urlserver + `/api_customer/edit?room_id=${id}`);
            console.log(response.data);
            set_edit_Room_id(response.data[0].room_id);
            set_edit_Cus_name(response.data[0].cus_name);
            set_edit_Cus_typeroom(response.data[0].cus_typeroom);
            set_edit_Cus_room(response.data[0].cus_room);
            set_edit_Cus_status(response.data[0].cus_status);
            set_edit_Cus_datein(response.data[0].cus_datein);



        } catch (error) {
            console.error(error);
        }
    };
    const deleteRow = async (id) => {

        try {
            const response = await axios.delete(urlserver + `/api_customer/delete?room_id=${id}`);
            console.log(response.data); // ตัวอย่างการใช้งาน response ที่ได้จาก server

            if (response.data !== "") {
                Swal({
                    title: '',
                    text: 'ลบข้อมูลสำเร็จ',
                    icon: 'success',
                });
                fetchUsers();
            }

        } catch (error) {
            console.error(error);
        }
    };
    const ob_status = {
        1: 'ว่าง',
        2: 'ไม่ว่าง',
    };

    const openModal = () => {
        setShowModal(true);

    };
    const closeModal = () => {
        setShowModal(false);
    };
    const openModaledit = (id) => {
        setShowModaledit(true);
        fetchedit(id)
    };
    const closeModaledit = () => {
        setShowModaledit(false);
    };
    const handleSignup = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post(urlserver + `/api_customer/create`, {
                room_id,
                cus_name,
                cus_room,
                cus_typeroom,
                cus_status,
                cus_datein,
            });
            // กระบวนการอื่นๆ เมื่อสำเร็จ

            console.log(response.data); // ตัวอย่างการใช้งาน response ที่ได้จาก server
            if (response.data !== "") {
                Swal({
                    title: '',
                    text: 'บันทึกข้อมูลสำเร็จ',
                    icon: 'success',
                });
                setShowModal(false);
            }
            fetchUsers();
        } catch (error) {
            console.error(error);
        }
    };
    const updatmodal = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.put(urlserver + `/api_customer/update`, {
                edit_room_id,
                edit_cus_name,
                edit_cus_room,
                edit_cus_typeroom,
                edit_cus_status,
                edit_cus_datein,
            });
            // กระบวนการอื่นๆ เมื่อสำเร็จ

            console.log(response.data); // ตัวอย่างการใช้งาน response ที่ได้จาก server
            if (response.data !== "") {
                Swal({
                    title: '',
                    text: 'แก้ไขข้อมูลสำเร็จ',
                    icon: 'success',
                });
                setShowModaledit(false);
            }
            fetchUsers();
        } catch (error) {
            console.error(error);
        }
    };

    return (
        <>
            <body className="g-sidenav-show   bg-gray-100">
                <Navbar />
                <main className="main-content position-relative border-radius-lg ">
                    {/* table */}
                    <br />
                    <div align="right" className=" container-fluid ">
                        <button type="button" className="btn btn-success" onClick={openModal} >
                            เพิ่มข้อมูล
                        </button>
                    </div>
                    <div className="container-fluid py2">
                        <div className="row">
                            <div className="col-12">
                                <div className="card mb-4">
                                    <div className="card-header pb-0">
                                        <h6>ข้อมูลห้องเช่า</h6>
                                    </div>
                                    <div className="card-body px-0 pt-0 pb-2">
                                        <div className="table-responsive p-0">
                                            <table className="table align-items-center mb-0">
                                                <thead>
                                                    <tr>
                                                        <th className="text-center  text-secondary text-xs font-weight-bolder opacity-7">ประเภทห้อง</th>
                                                        <th className="text-center  text-secondary text-xs font-weight-bolder opacity-7 ps-2">ห้องที่</th>
                                                        <th className="text-center  text-secondary text-xs font-weight-bolder opacity-7">สถานะห้อง</th>
                                                        <th colSpan={2} className="text-center opacity-7">#</th>
                                                    </tr>
                                                </thead>
                                                <tbody>
                                                    {currentUsers.map((file, index) => (
                                                        <tr key={index}>
                                                            <td className="text-center  text-sm font-weight-bolder opacity-8">
                                                                {file.cus_name}
                                                            </td>
                                                            <td className="text-center text-sm font-weight-bolder opacity-8">
                                                                {file.cus_room + " " + file.cus_typeroom}
                                                            </td>

                                                            {file.cus_status === 1 ? (
                                                                <td className="text-center ">
                                                                    <span style={{ width: '70px' }} className="badge badge-sm bg-gradient-success">{ob_status[file.cus_status]}</span>
                                                                </td>
                                                            ) : (
                                                                <td className="text-center ">
                                                                    <span style={{ width: '70px' }} className="badge badge-sm bg-gradient-danger">{ob_status[file.cus_status]}</span>
                                                                </td>
                                                            )}
                                                            <td className="text-center ">
                                                                <button className="btn btn-primary" type='button' onClick={() => openModaledit(file.room_id)}>
                                                                    <i className="fa-regular fa-pen-to-square"></i>
                                                                </button>
                                                                &nbsp;
                                                                <button className="btn btn-danger" type='button' onClick={() => deleteRow(file.room_id)}>
                                                                    <i className="fa-solid fa-trash-can"></i>
                                                                </button>
                                                            </td>
                                                        </tr>
                                                    ))}
                                                </tbody>
                                            </table>
                                        </div>
                                    </div>
                                    <ul className="pagination justify-content-end">
                                        {Array.from({ length: totalPages }, (_, i) => i + 1).map((page) => (
                                            <li className={`page-item ${currentPage === page ? 'active' : ''}`}
                                                key={page}
                                                onClick={() => changePage(page)}>
                                                <span className="page-link">{page}</span>
                                            </li>
                                        ))}
                                    </ul>

                                </div>
                            </div>
                        </div>
                    </div>
                </main>
            </body >

            {/* <add> */}
            <Modal show={showModal} onHide={closeModal}>
                <Modal.Header closeButton>
                    <Modal.Title>เพิ่มข้อมูล</Modal.Title>
                </Modal.Header>
                <Modal.Body >
                    <Form onSubmit={handleSignup}>
                        <Form.Group hidden >
                            <Form.Label>room_id</Form.Label>
                            <Form.Control
                                type="text"
                                value={room_id}
                                onChange={(e) => setroom_id(e.target.value)}
                            />
                        </Form.Group>
                        <Form.Group>
                            <Form.Label>ประเภทห้อง</Form.Label>
                            <Form.Control
                                type="text"
                                value={cus_typeroom}
                                onChange={(e) => setCus_typeroom(e.target.value)}
                            />
                        </Form.Group>
                        <Form.Group>
                            <Form.Label>เลขห้อง</Form.Label>
                            <Form.Control
                                type="text"
                                value={cus_room}
                                onChange={(e) => setCus_room(e.target.value)}
                            />
                        </Form.Group>
                       
                        <Form.Group>
                            <Form.Label>สถานะ</Form.Label>
                            <Form.Control
                                as="select"
                                value={cus_status}
                                onChange={(e) => setCus_status(e.target.value)}>
                                <option value="1" >ว่าง</option>
                                <option value="2">ไม่ว่าง</option>

                            </Form.Control>
                        </Form.Group>
                        
                        <Modal.Footer>
                            <Button variant="success" type="submit" >
                                ยืนยัน
                            </Button>
                            <Button variant="danger" onClick={closeModal}>
                                ปิด
                            </Button>
                        </Modal.Footer>
                    </Form>
                </Modal.Body>
            </Modal>

            {/* <edit> */}
            <Modal show={showModaledit} onHide={closeModal}>
                <Modal.Header closeButton>
                    <Modal.Title>แก้ไขข้อมูล</Modal.Title>
                </Modal.Header>
                <Modal.Body >
                    <Form onSubmit={updatmodal}>
                        <Form.Group hidden >
                            <Form.Label>room_id</Form.Label>
                            <Form.Control
                                type="text"
                                value={edit_room_id}
                                onChange={(e) => set_edit_Room_id(e.target.value)}
                            />
                        </Form.Group>
                        <Form.Group>
                            <Form.Label>ประเภทห้อง</Form.Label>
                            <Form.Control
                                type="text"
                                value={edit_cus_name}
                                onChange={(e) => set_edit_Cus_name(e.target.value)}
                            />
                        </Form.Group>
                     
                        <Form.Group>
                            <Form.Label>เลขห้อง</Form.Label>
                            <Form.Control
                                type="text"
                                value={edit_cus_room}
                                onChange={(e) => set_edit_Cus_room(e.target.value)}
                            />
                        </Form.Group>
                        <Form.Group>
                            <Form.Label>สถานะ</Form.Label>
                            <Form.Control
                                as="select"
                                value={edit_cus_status}
                                onChange={(e) => set_edit_Cus_status(e.target.value)}>
                                <option value="1" >ว่าง</option>
                                <option value="2">ไม่ว่าง</option>

                            </Form.Control>
                        </Form.Group>
                       
                        <Modal.Footer>
                            <Button variant="success" type="submit" >
                                ยืนยัน
                            </Button>
                            <Button variant="danger" onClick={closeModaledit}>
                                ปิด
                            </Button>
                        </Modal.Footer>
                    </Form>
                </Modal.Body>
            </Modal>
        </>
    )
};

export default Customer
