import React, { useState, useEffect } from 'react'
import Navbar from "../Navbar";
import axios from 'axios';
import { Button, Modal, Form } from 'react-bootstrap';
function Dashboard() {
  const [cus_id, setcus_id] = useState();
  const [cus_name, setcus_name] = useState("");
  const [cus_room_id, setcus_room_id] = useState("");
  const [cus_status, setcus_status] = useState(1);
  const [cus_datein, setcus_datein] = useState(formattedDate);
  const [cus_room_price, set_cus_room_price] = useState("");
  const [cus_room_guarantee, set_cus_room_guarantee] = useState("");
  const [cus_room_advance, set_cus_room_advance] = useState("");
  const [cus_room_water, set_cus_room_water] = useState("");
  const [cus_room_electricity, set_cus_room_electricity] = useState("");
  const [cus_room_bin_price, set_cus_room_bin_price] = useState("");
  const [cus_room_sum, set_cus_room_sum] = useState("");

  const [data, setData] = useState([]);
  const urlserver = "http://localhost:4000";
  //const urlserver = "https://backendrental.onrender.com";
  useEffect(() => {
    return () => {
      fetchUsers();

    }
  }, []);


  const fetchUsers = async () => {
    try {
      await axios.post(urlserver + `/api_payment/create`);
      const response = await axios.get(urlserver + `/api_payment`);
      setData(response.data);
      console.log(response.data);
    } catch (error) {
      console.error(error);
    }
  };
  return (
    <>
      <body className="g-sidenav-show   bg-gray-100">
        <Navbar />
        <main className="main-content position-relative border-radius-lg ">

          <div className="container-fluid py-4 ">
            <div className="row">
              <div className="col-xl-3 col-sm-6 mb-xl-0 mb-4">
                <div className="card">
                  <div className="card-body p-3">
                    <div className="row">
                      <div className="col-8">
                        <div className="numbers">
                          <p className="text-sm mb-0 text-uppercase font-weight-bold">ยอดที่ค้างชำระทั้งหมด</p>
                          <h5 className="font-weight-bolder">
                            <span className="text-danger text-m font-weight-bolder">5,000 </span>
                            บาท

                          </h5>

                          <p className="mb-0">

                          </p>
                        </div>
                      </div>
                      <div className="col-4 text-end">
                        <div className="icon icon-shape bg-gradient-danger shadow-primary text-center rounded-circle">
                          <i className="ni ni-money-coins text-lg opacity-10" aria-hidden="true"></i>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
              <div className="col-xl-3 col-sm-6 mb-xl-0 mb-4">
                <div className="card">
                  <div className="card-body p-3">
                    <div className="row">
                      <div className="col-8">
                        <div className="numbers">
                          <p className="text-sm mb-0 text-uppercase font-weight-bold">ยอดที่ได้รับทั้งสิ้น</p>
                          <h5 className="font-weight-bolder">
                            <span className="text-success text-m font-weight-bolder">10,000 </span>
                            บาท
                          </h5>
                          <p className="mb-0">

                          </p><i class="bi bi-cash-coin"></i>
                        </div>
                      </div>
                      <div className="col-4 text-end">
                        <div className="icon icon-shape bg-gradient-success shadow-danger text-center rounded-circle">
                          <i className="ni ni-money-coins text-lg opacity-10" aria-hidden="true"></i>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>

            </div>
          </div>

          {/* table */}
          <div className="container-fluid py-4">
            <div className="row">
              <div className="col-12">
                <div className="card mb-4">
                  <div className="card-header pb-0">
                    <h6>ค่าเช่าที่ต้องเก็บ</h6>
                  </div>
                  <div className="card-body px-0 pt-0 pb-2">
                    <div className="table-responsive p-0">
                      <table className="table align-items-center mb-0">
                        <thead>
                          <tr>
                            <th className="text-center  text-secondary text-xs font-weight-bolder opacity-7">ชื่อ<br />เลขห้อง<br />ประเภทห้อง</th>
                            <th className="text-center  text-secondary text-xs font-weight-bolder opacity-7">ยอดที่<br />ต้องชำระ</th>
                            <th className="text-center  text-secondary text-xs font-weight-bolder opacity-7">งวด<br />เดือน/ปี</th>
                            <th className="text-center opacity-7">#</th>
                          </tr>
                        </thead>
                        <tbody>
                          {data.map((data, index) => (
                            <tr key={index}>
                              <td className="text-center  text-xs font-weight-bolder opacity-8">
                                {data.pay_cus_name}<br />{data.roomData.room_name + ' ' + data.roomData.room_typename}
                              </td>
                              <td className="text-center text-xs font-weight-bolder opacity-8">
                                {data.pay_cus_room_sum.toLocaleString()}
                              </td>
                              <td className="text-center text-xs font-weight-bolder opacity-8">
                                {data.pay_round}
                              </td>
                              <td className="text-center text-xs font-weight-bolder opacity-8"></td>
                            </tr>
                          ))}
                        </tbody>

                      </table>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
  {/* <add> */}
  <Modal show={showModal} onHide={closeModal}>
                <Modal.Header closeButton>
                    <Modal.Title>เพิ่มข้อมูล</Modal.Title>
                </Modal.Header>
                <Modal.Body >
                    <Form onSubmit={handleSignup}>
                        <Form.Group hidden >
                            <Form.Label>cus_id</Form.Label>
                            <Form.Control
                                type="text"
                                value={cus_id}
                                onChange={(e) => setcus_id(e.target.value)} />
                        </Form.Group>

                        <Form.Group >
                            <Form.Label ><span style={{ color: 'red' }}>*</span> ชื่อ-นามสกุล</Form.Label>
                            <Form.Control
                                type="text"
                                value={cus_name}
                                onChange={(e) => setcus_name(e.target.value)}
                            />
                        </Form.Group>

                        <Form.Group >
                            <Form.Label>ประเภทห้อง/เลขห้อง</Form.Label>
                            <Form.Control
                                as="select"
                                value={cus_room_id}
                                onChange={(e) => {
                                    setcus_room_id(e.target.value);
                                    fettyperoom(e.target.value, "add");
                                }}>
                                {dataroom
                                    .filter((option) => {
                                        // กรองข้อมูลที่ต้องการโดยใช้เงื่อนไข if-else
                                        if (option.room_status === 1) {
                                            return true; // แสดง Element นี้
                                        } else {
                                            return false; // ไม่แสดง Element นี้
                                        }
                                    })
                                    .map((option) => (
                                        <option key={option.room_id} value={option.room_id}>
                                            {option.room_name + " " + option.room_typename}
                                        </option>
                                    ))}
                            </Form.Control>
                        </Form.Group>
                        <div style={{ display: 'flex', alignItems: 'center' }}>
                            <div style={{ flex: '1', marginRight: '10px' }}>
                                <Form.Group>
                                    <Form.Label><span style={{ color: 'red' }}>*</span> ค่าห้อง</Form.Label>
                                    <Form.Control
                                        type="text"
                                        value={cus_room_price}
                                        onChange={(e) => {
                                            const inputValue = e.target.value.replace(/\D/g, ''); // เอาแต่ตัวเลข
                                            const formattedValue = Number(inputValue).toLocaleString('en-US');
                                            set_cus_room_price(formattedValue);
                                            sum_total("add");

                                        }}
                                    />
                                </Form.Group>
                            </div>

                            <div style={{ flex: '1', marginRight: '10px' }}>
                                <Form.Group>
                                    <Form.Label><span style={{ color: 'red' }}>*</span> ค่ามัดจำล่วงหน้า</Form.Label>
                                    <Form.Control
                                        type="text"
                                        value={cus_room_advance}
                                        onChange={(e) => {
                                            const inputValue = e.target.value.replace(/\D/g, ''); // เอาแต่ตัวเลข
                                            const formattedValue = Number(inputValue).toLocaleString('en-US');
                                            set_cus_room_advance(formattedValue);
                                            sum_total("add");

                                        }}
                                    />
                                </Form.Group>
                            </div>
                        </div>
                        <Form.Group>
                            <Form.Label><span style={{ color: 'red' }}>*</span> ค่าประกัน</Form.Label>
                            <Form.Control
                                type="text"
                                value={cus_room_guarantee}
                                onChange={(e) => {
                                    const inputValue = e.target.value.replace(/\D/g, ''); // เอาแต่ตัวเลข
                                    const formattedValue = Number(inputValue).toLocaleString('en-US');
                                    set_cus_room_guarantee(formattedValue);
                                    sum_total("add");
                                }}
                            />
                        </Form.Group>

                        <div style={{ display: 'flex', alignItems: 'center' }} hidden>
                            <div style={{ flex: '1', marginRight: '10px' }}>
                                <Form.Group>
                                    <Form.Label><span style={{ color: 'red' }}>*</span> ค่าน้ำ</Form.Label>
                                    <Form.Control
                                        type="text"
                                        value={cus_room_water}
                                        onChange={(e) => {
                                            const inputValue = e.target.value.replace(/\D/g, '');
                                            const formattedValue = Number(inputValue).toLocaleString('en-US');
                                            set_cus_room_water(formattedValue);
                                        }}
                                    />
                                </Form.Group>
                            </div>
                            <div style={{ flex: '1', marginLeft: '10px' }}>
                                <Form.Group>
                                    <Form.Label><span style={{ color: 'red' }}>*</span> ค่าไฟฟ้า</Form.Label>
                                    <Form.Control
                                        type="text"
                                        value={cus_room_electricity}
                                        onChange={(e) => {
                                            const inputValue = e.target.value.replace(/\D/g, '');
                                            const formattedValue = Number(inputValue).toLocaleString('en-US');
                                            set_cus_room_electricity(formattedValue);
                                        }}
                                    />
                                </Form.Group>
                            </div>
                            <div style={{ flex: '1', marginLeft: '10px' }}>
                                <Form.Group>
                                    <Form.Label><span style={{ color: 'red' }}>*</span> ค่าขยะ</Form.Label>
                                    <Form.Control
                                        type="text"
                                        value={cus_room_bin_price}
                                        onChange={(e) => {
                                            const inputValue = e.target.value.replace(/\D/g, ''); // เอาแต่ตัวเลข
                                            const formattedValue = Number(inputValue).toLocaleString('en-US');
                                            set_cus_room_bin_price(formattedValue);
                                        }}
                                    />
                                </Form.Group>
                            </div>
                        </div>

                        <Form.Group  >
                            <Form.Label><span style={{ color: 'red' }}>*</span> ยอดที่ต้องชำระ</Form.Label>
                            <Form.Control style={{ backgroundColor: '#42bd41', color: 'white' }}
                                type="text"
                                value={cus_room_sum}
                                onChange={(e) => {
                                    const inputValue = e.target.value.replace(/\D/g, ''); // เอาแต่ตัวเลข
                                    const formattedValue = Number(inputValue).toLocaleString('en-US');
                                    set_cus_room_sum(formattedValue);

                                }}
                            />
                        </Form.Group>




                        <Form.Group hidden>
                            <Form.Label>สถานะ</Form.Label>
                            <Form.Control disabled
                                as="select"
                                value={cus_status}
                                onChange={(e) => setcus_status(e.target.value)}>
                                <option value="1" >พักอาศัย</option>
                                <option value="2">ออก</option>
                            </Form.Control>
                        </Form.Group>

                        <Form.Group>
                            <Form.Label>วันที่เข้าพัก</Form.Label>
                            <Form.Control
                                type="date"
                                value={cus_datein}
                                onChange={(e) => setcus_datein(e.target.value)}
                            />
                        </Form.Group>
                        <Modal.Footer>
                            <Button variant="success" type="submit" >
                                ยืนยัน
                            </Button>
                            <Button variant="danger" onClick={closeModal}>
                                ปิด
                            </Button>
                        </Modal.Footer>
                    </Form>
                </Modal.Body>
            </Modal>

        </main>
      </body>
    </>
  )

}

export default Dashboard
