import React, { useState, useEffect } from 'react'

import axios from 'axios';
function App() {
  const [orderDetails, setOrderDetails] = useState({ items: '' });
  const [pdfBlob, setPdfBlob] = useState(null);
  const urlserver = "http://localhost:4000";
  const generateReceipt = async () => {
    try {
      const response = await axios.post(urlserver + `/api_report_dashboard/generate-receipt`, { orderDetails });
      const pdfBlob = new Blob([response.data], { type: 'application/pdf' });
      setPdfBlob(pdfBlob);
    } catch (error) {
      console.error(error);
    }
  };

  return (

    <div>

      <h1>แอปพลิเคชันสร้างใบเสร็จ</h1>
      <div>
        <label>รายการสินค้า: </label>
        <input
          type="text"
          value={orderDetails.items}
          onChange={(e) => setOrderDetails({ items: e.target.value })}
        />
      </div>
      <button onClick={generateReceipt}>สร้างใบเสร็จ</button>
      {pdfBlob && (
        <div>
          <h2>ใบเสร็จ</h2>
          <embed src={URL.createObjectURL(pdfBlob)} type="application/pdf" width="100%" height="500px" />
        </div>
      )}
    </div>
  );
}

export default App;
