import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Button, Modal, Form } from 'react-bootstrap';
import axios from 'axios';
import Swal from 'sweetalert';
const SignupForm = () => {
  const [searchdata, setsearchdata] = useState('');
  const [status, setstatus] = useState('1');
  const [name, setName] = useState('');
  const [surname, setSurname] = useState('');
  const [username, setUsername] = useState('');
  const [password,  setPassword] = useState('');
  const [phone, setPhone] = useState('');
  const [address, setAddress] = useState('');
  const [editid, seteditid] = useState('');
  const [editname, seteditName] = useState('');
  const [editsurname, seteditSurname] = useState('');
  const [editusername,  seteditUsername] = useState('');
  const [editpassword, seteditPassword] = useState('');
  const [editphone, seteditPhone] = useState('');
  const [editaddress, seteditAddress] = useState('');
  const [editstatus, seteditstatus] = useState('');
  const [users, setUsers] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [ShowEditModal, setShowEditModal] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [image, setImage] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [selectedIds, setSelectedIds] = useState([]);
  const [selectAll, setSelectAll] = useState(false);
  const urlserver = "http://localhost:4000";
  //const urlserver = "https://nodejs-ktey.onrender.com";


  const handleCheckboxChange = (e) => {
    const id = parseInt(e.target.value);

    if (e.target.name === 'selectAll') {
      setSelectAll(e.target.checked);
      setSelectedIds(
        e.target.checked ? users.map((user) => user.id) : []
      );
    } else {
      setSelectedIds((prevSelectedIds) =>
        e.target.checked
          ? [...prevSelectedIds, id]
          : prevSelectedIds.filter((selectedId) => selectedId !== id)
      );
    }
  };

  //const urlserver="";
  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    setImage(file);
    const reader = new FileReader();
    reader.onloadend = () => {
      setImagePreview(reader.result);
    };
    reader.readAsDataURL(file);
  };

  useEffect(() => {

    if (searchdata === '') {
      fetchUsers();
    }
    if (searchdata !== '') {
      fetchsearch(searchdata);
    }
  }, [searchdata]);
  const fetchsearch = async (searchdata) => {
    try {
      const response = await axios.get(urlserver + `/register/search?searchdata=${searchdata}`);
      setUsers(response.data);
      setTotalPages(Math.ceil(response.data.length / 10));
    } catch (error) {
      console.error(error);
    }
  };
  const fetchUsers = async () => {
    try {
      const response = await axios.get(urlserver + '/register/');
      setUsers(response.data);
      setTotalPages(Math.ceil(response.data.length / 10));
    } catch (error) {
      console.error(error);
    }
  };
  const fetchedit = async (id) => {
    try {
      const response = await axios.get(urlserver + `/register/get/?id=${id}`);
      if (response.data && response.data.length > 0) {
        seteditName(response.data[0].first_name);
        seteditSurname(response.data[0].last_name);
        seteditUsername(response.data[0].email);
        seteditPassword(response.data[0].password);
        seteditPhone(response.data[0].tel);
        seteditAddress(response.data[0].home);
        seteditstatus(response.data[0].status);
        seteditid(id);
      }
    } catch (error) {
      console.error(error);
    }
  };
  const showAlertDelete = () => {

    Swal({
      title: '',
      text: 'ลบข้อมูลสำเร็จ',
      icon: 'success',
    });
  };
  const handleSignup = async (e) => {
    e.preventDefault();
    if (name.trim() === '') {
      Swal({
        title: '',
        text: 'กรุณากรอกชื่อ',
        icon: 'error',
      });
      return;
    }

    const userData = {
      name,
      surname,
      username,
      password,
      phone,
      address,
      status,
    };

    try {
      const response = await axios.post(urlserver + '/register/', userData);
      console.log(response.data);

      // อัปโหลดไฟล์ภาพเป็นขั้นตอนแยก
      const userId = response.data.insertId;
      const formData = new FormData();
      formData.append('image', image);

      const uploadResponse = await axios.post(urlserver + `/register/pic/?id=${userId}`, formData);
      console.log(uploadResponse.data);
      setTimeout(() => {
        Swal({
          title: '',
          text: 'บันทึกข้อมูลสำเร็จ',
          icon: 'success',
        });
  

        fetchUsers();

      }, 500); // 1000 มีแสดงถึงเวลาที่ต้องการ (ในกรณีนี้คือ 1 วินาที)

      setName('');
      setSurname('');
      setUsername('');
      setPassword('');
      setPhone('');
      setAddress('');
      setstatus('');

      setShowModal(false);
      setShowEditModal(false);
    } catch (error) {
      console.error(error);
    }
  };


  const sendmailall = async (e) => {
    e.preventDefault();
    if (e.nativeEvent.submitter.innerText === 'ส่งเมล์') {
      // ตรงนี้คือโค้ดที่ต้องการให้ทำงานเมื่อคลิกปุ่ม "ส่งเมล์"
      try {
        const response = await axios.get(urlserver + '/register/sendmail_all', {
          params: { ids: selectedIds },
        });
        console.log(response.data);
        Swal({
          title: '',
          text: 'ส่งเมล์สำเร็จ',
          icon: 'success',
        });
        fetchUsers();
      } catch (error) {
        console.error(error);
      }
      // กระบวนการส่งเมล์หรือโค้ดที่ต้องการให้ทำทำงานอื่น ๆ
    } else if (e.nativeEvent.submitter.innerText === 'ลบข้อมูล') {
      // ตรงนี้คือโค้ดที่ต้องการให้ทำงานเมื่อคลิกปุ่ม "ลบข้อมูล"
      try {
        const response = await axios.delete(urlserver + '/register/delete_all', {
          params: { ids: selectedIds },
        });
        console.log(response.data);
        Swal({
          title: '',
          text: 'ลบข้อมูลสำเร็จ',
          icon: 'success',
        });
        fetchUsers();
      } catch (error) {
        console.error(error);
      }
    }

  };

  const handleEdit = async (e) => {
    e.preventDefault();
    const edituserData = {
      editname,
      editsurname,
      editusername,
      editpassword,
      editphone,
      editaddress,
      editstatus,
      editid,
    };
    try {
      const response = await axios.put(urlserver + '/register/', edituserData);
      console.log(response.data);
      fetchUsers();
      Swal({
        title: '',
        text: 'แก้ไขข้อมูลสำเร็จ',
        icon: 'success',
      });
      setShowEditModal(false);
    } catch (error) {
      console.error(error);
    }
  };
  const openModal = () => {
    setShowModal(true);
  };
  const openEditModal = (id) => {
    fetchedit(id);
    setShowEditModal(true);
  };
  const closeModal = () => {
    setShowModal(false);
  };
  const closeModaledit = () => {
    setShowEditModal(false);
  };

  const deleterecord = async (id) => {
    try {
      const response = await axios.delete(urlserver + `/register/?id=${id}`);
      console.log(response.data);
      showAlertDelete();
      fetchUsers();
    } catch (error) {
      console.error(error);
    }
  };
  const startIndex = (currentPage - 1) * 10;
  const endIndex = currentPage * 10;
  const currentUsers = users.slice(startIndex, endIndex);
  const changePage = (page) => {
    setCurrentPage(page);
  };
  return (
    <div className="container">
      <h3>ข้อมูลสมาชิก</h3>
      <div className="d-flex justify-content-start">
        <button type="button" className="btn btn-primary" onClick={openModal}>
          เพิ่มข้อมูล
        </button>
      </div>
      <br></br>
      <div className="d-flex justify-content-start">

        <Form.Label style={{ marginRight: '10px' }}>ค้นหา : </Form.Label>
        <Form.Control
          style={{ width: '300px' }}
          type="text"
          value={searchdata}
          onChange={(e) => setsearchdata(e.target.value)}
        />
      </div>
      <Modal show={showModal} onHide={closeModal}>
        <Modal.Header closeButton>
          <Modal.Title>เพิ่มข้อมูล</Modal.Title>
        </Modal.Header>
        <Modal.Body >
          <Form onSubmit={handleSignup}>


            <Form.Group>
              <Form.Label>Name</Form.Label>
              <Form.Control
                type="text"
                value={name}
                onChange={(e) => setName(e.target.value)}
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>Surname</Form.Label>
              <Form.Control
                type="text"
                value={surname}
                onChange={(e) => setSurname(e.target.value)}
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>email</Form.Label>
              <Form.Control
                type="text"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>Password</Form.Label>
              <Form.Control
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>Phone</Form.Label>
              <Form.Control
                type="tel"
                value={phone}
                onChange={(e) => setPhone(e.target.value)}
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>Address</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                value={address}
                onChange={(e) => setAddress(e.target.value)}
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>status</Form.Label>
              <Form.Control
                as="select"
                value={status}
                onChange={(e) => setstatus(e.target.value)}>
                <option value="">-- เลือกสถานะ --</option>
                <option value="1" >Active</option>
                <option value="2">Inactive</option>
                <option value="3" >Pending</option>
              </Form.Control>
            </Form.Group>
            <Form.Group>
              <Form.Label>รูปภาพ</Form.Label>  {imagePreview && <img src={imagePreview} alt="รูปภาพ" style={{ width: '50px' }} />}
              <Form.Control type="file" onChange={handleImageUpload} />

            </Form.Group>
            <Modal.Footer>
              <Button variant="primary" type="submit" >
                ยืนยัน
              </Button>
              <Button variant="secondary" onClick={closeModal}>
                ปิด
              </Button>
            </Modal.Footer>
          </Form>
        </Modal.Body>
      </Modal>

      <Modal show={ShowEditModal} onHide={closeModaledit}>
        <Modal.Header closeButton>
          <Modal.Title>แก้ไขข้อมูล</Modal.Title>
        </Modal.Header>
        <Modal.Body >
          <Form onSubmit={handleEdit}>
            <Form.Group hidden>
              <Form.Label>editid</Form.Label>
              <Form.Control
                type="hidden"
                value={editid}
                onChange={(e) => seteditName(e.target.value)}
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>Name</Form.Label>
              <Form.Control
                type="text"
                value={editname}
                onChange={(e) => seteditName(e.target.value)}
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>Surname</Form.Label>
              <Form.Control
                type="text"
                value={editsurname}
                onChange={(e) => seteditSurname(e.target.value)}
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>email</Form.Label>
              <Form.Control
                type="text"
                value={editusername}
                onChange={(e) => seteditUsername(e.target.value)}
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>Password</Form.Label>
              <Form.Control
                type="password"
                value={editpassword}
                onChange={(e) => seteditPassword(e.target.value)}
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>Phone</Form.Label>
              <Form.Control
                type="tel"
                value={editphone}
                onChange={(e) => seteditPhone(e.target.value)}
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>Address</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                value={editaddress}
                onChange={(e) => seteditAddress(e.target.value)}
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>status</Form.Label>
              <Form.Control
                as="select"
                value={editstatus}
                onChange={(e) => seteditstatus(e.target.value)}
              >
                <option value="">-- เลือกสถานะ --</option>
                <option value="1"  >Active</option>
                <option value="2">Inactive</option>
                <option value="3" >Pending</option>
              </Form.Control>


            </Form.Group>

            <Modal.Footer>
              <Button variant="primary" type="submit" >
                ยืนยัน
              </Button>
              <Button variant="secondary" onClick={closeModaledit}>
                ปิด
              </Button>
            </Modal.Footer>
          </Form>

        </Modal.Body>
      </Modal>
      <Form onSubmit={sendmailall} >
        <table className="table" border={0} style={{ width: '100%' }}>
          <thead>
            <tr>
              <th >
                <input
                  type='checkbox'
                  name='selectAll'
                  checked={selectAll}
                  onChange={handleCheckboxChange}
                />
              </th>
              <th>ชื่อ</th>
              <th>นามสกุล</th>
              <th>อีเมล์</th>
              <th>รหัสผ่าน</th>
              <th>เบอร์โทรศัพท์</th>
              <th>ที่อยู่</th>
              <th>รูปภาพ</th>
              <th>#</th>
            </tr>
          </thead>
          <tbody>
            {currentUsers.map((user, index) => (

              <tr key={user.id}>
                <td><input type='checkbox'
                  value={user.id}
                  checked={selectedIds.includes(user.id)}
                  onChange={handleCheckboxChange}
                /></td>
                <td>{user.first_name}</td>
                <td>{user.last_name}</td>
                <td>{user.email}</td>
                <td>{user.password}</td>
                <td>{user.tel}</td>
                <td>{user.home}</td>

                {user.pic ? (
                  <td><img style={{ width: '50px' }} src={require(`./resume/${user.pic}`)} alt="รูปภาพ" /></td>
                ) : (
                  <td></td>
                )}

                <td></td>
                <td>
                  <Button variant="warning" onClick={() => openEditModal(user.id)}>
                    แก้ไข
                  </Button>
                  <Button variant="danger" onClick={() => deleterecord(user.id)}>
                    ลบ
                  </Button>
                </td>
              </tr>

            ))}
          </tbody>
        </table>

        {/* Pagination */}

        <nav aria-label="Page navigation">


          <Button variant="success" type="submit"  > ส่งเมล์ </Button>
          <Button variant="danger" type="submit" > ลบข้อมูล </Button>

          <ul className="pagination justify-content-end">

            {Array.from({ length: totalPages }, (_, i) => i + 1).map((page) => (
              <li
                className={`page-item ${currentPage === page ? 'active' : ''}`}
                key={page}
                onClick={() => changePage(page)}
              >
                <span className="page-link">{page}</span>
              </li>
            ))}
          </ul>
        </nav>
      </Form>
    </div>
  );
};

export default SignupForm;
