const axios = require("axios");

export default function notify() {

  // ส่ง API ไปยัง Line Notify
  const sendLineNotify = async (messagedata) => {
    try {

      const headers = {
        'Content-Type': 'application/x-www-form-urlencoded',
        'Authorization': `Bearer f8Qh0NgsqcDLmbJvQS1qhN7nWoiBVBuHGBWm3DMoM5Q`
      };
      const message = messagedata;
      const params = new URLSearchParams();
      params.append('message', message);

      await axios.post("https://notify-bot.line.me/oauth/token", params, { headers });
      console.log('Line Notify sent successfully!');
    } catch (error) {
      console.error('Failed to send Line Notify:', error.message);
    }
  };

}
