import axios from 'axios';

const LINE_NOTIFY_API_URL = 'https://notify-api.line.me/api/notify';
const LINE_NOTIFY_ACCESS_TOKEN = 'YOUR_LINE_NOTIFY_ACCESS_TOKEN'; // ใส่ Access Token ที่คุณได้รับจาก Line Notify ที่นี่


export const aaa = async (message) => {
  try {
    const headers = {
      'Content-Type': 'application/x-www-form-urlencoded',
      'Authorization': `Bearer ${LINE_NOTIFY_ACCESS_TOKEN}`
    };

    const params = new URLSearchParams();
    params.append('message', message);

    await axios.post(LINE_NOTIFY_API_URL, params, { headers });
    console.log('Line Notify sent successfully!');
  } catch (error) {
    console.error('Failed to send Line Notify:', error.message);
  }
};
