import React, { useState, useEffect } from 'react'
import Navbar from "../Navbar";
import axios from 'axios';
import { Button, Modal } from 'react-bootstrap';
function Dashboard() {
  const [selectedPaymentMethod, setSelectedPaymentMethod] = useState(1);
  const [pic_qr_code, set_pic_qr_code] = useState(1);
  const [ownerName, set_ownerName] = useState(1);
  const [amount, set_amount] = useState(1);

  const [data, setData] = useState([]);
  // const [showModal, setShowModal] = useState(false);
  const [showModaledit, setShowModaledit] = useState(false);
  const urlserver = "http://localhost:4000";
  //const urlserver = "https://backendrental.onrender.com";
  useEffect(() => {
    return () => {
      fetchUsers();

    }
  }, []);


  const fetchUsers = async () => {
    try {
      await axios.post(urlserver + `/api_payment/create`);
      const response = await axios.get(urlserver + `/api_payment`);
      setData(response.data);
      console.log(response.data);
    } catch (error) {
      console.error(error);
    }
  };
  const deleteRow = async (id) => {
  };
  const openModaledit = (id) => {
    setShowModaledit(true);
  };
  const closeModal = () => {
    setShowModaledit(false);
  };
  const qr_code = async () => {

    try {
      const response = await axios.get(urlserver + `/api_payment/qr_code`);
      console.log(response.data.qrData);
      set_pic_qr_code(response.data.qrData);
      set_ownerName();
      set_amount();
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <>
      <body className="g-sidenav-show   bg-gray-100">
        <Navbar />
        <main className="main-content position-relative border-radius-lg ">

          <div className="container-fluid py-4 ">
            <div className="row">
              <div className="col-xl-3 col-sm-6 mb-xl-0 mb-4">
                <div className="card">
                  <div className="card-body p-3">
                    <div className="row">
                      <div className="col-8">
                        <div className="numbers">
                          <p className="text-sm mb-0 text-uppercase font-weight-bold">ยอดที่ค้างชำระทั้งหมด</p>
                          <h5 className="font-weight-bolder">
                            <span className="text-danger text-m font-weight-bolder">5,000 </span>
                            บาท

                          </h5>

                          <p className="mb-0">

                          </p>
                        </div>
                      </div>
                      <div className="col-4 text-end">
                        <div className="icon icon-shape bg-gradient-danger shadow-primary text-center rounded-circle">
                          <i className="ni ni-money-coins text-lg opacity-10" aria-hidden="true"></i>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
              <div className="col-xl-3 col-sm-6 mb-xl-0 mb-4">
                <div className="card">
                  <div className="card-body p-3">
                    <div className="row">
                      <div className="col-8">
                        <div className="numbers">
                          <p className="text-sm mb-0 text-uppercase font-weight-bold">ยอดที่ได้รับทั้งสิ้น</p>
                          <h5 className="font-weight-bolder">
                            <span className="text-success text-m font-weight-bolder">10,000 </span>
                            บาท
                          </h5>
                          <p className="mb-0">

                          </p><i class="bi bi-cash-coin"></i>
                        </div>
                      </div>
                      <div className="col-4 text-end">
                        <div className="icon icon-shape bg-gradient-success shadow-danger text-center rounded-circle">
                          <i className="ni ni-money-coins text-lg opacity-10" aria-hidden="true"></i>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>

            </div>
          </div>

          {/* table */}
          <div className="container-fluid py-4">
            <div className="row">
              <div className="col-12">
                <div className="card mb-4">
                  <div className="card-header pb-0">
                    <h6>ค่าเช่าที่ต้องเก็บ</h6>
                  </div>
                  <div className="card-body px-0 pt-0 pb-2">
                    <div className="table-responsive p-0">
                      <table className="table align-items-center mb-0">
                        <thead>
                          <tr>
                            <th className="text-center  text-secondary text-xs font-weight-bolder opacity-7">ชื่อ<br />เลขห้อง<br />ประเภทห้อง</th>
                            <th className="text-center  text-secondary text-xs font-weight-bolder opacity-7">ยอดที่<br />ต้องชำระ</th>
                            <th className="text-center  text-secondary text-xs font-weight-bolder opacity-7">งวด<br />เดือน/ปี</th>
                            <th className="text-center opacity-7">#</th>
                          </tr>
                        </thead>
                        <tbody>
                          {data.map((data, index) => (
                            <tr key={index}>
                              <td className="text-center  text-xs font-weight-bolder opacity-8">
                                {data.pay_cus_name}<br />{data.roomData.room_name + ' ' + data.roomData.room_typename}
                              </td>
                              <td className="text-center text-xs font-weight-bolder opacity-8">
                                {data.pay_cus_room_sum.toLocaleString()}
                              </td>
                              <td className="text-center text-xs font-weight-bolder opacity-8">
                                {data.pay_round}
                              </td>
                              <td className="text-center ">
                                <button className="btn btn-info" type='button' onClick={() => openModaledit(data.pay_id)}>
                                  <i className="fa-regular fa-pen-to-square"></i>
                                </button>
                                &nbsp;
                                <button className="btn btn-danger" type='button' onClick={() => deleteRow(data.pay_id)}>
                                  <i className="fa-solid fa-trash-can"></i>
                                </button>
                              </td>
                            </tr>
                          ))}
                        </tbody>

                      </table>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
          {/* <add> */}
          <Modal show={showModaledit} onHide={closeModal}>
            <Modal.Header closeButton>
              <Modal.Title>ชำระเงิน</Modal.Title>
            </Modal.Header>
            <Modal.Body className="d-flex flex-column">
              <div className="d-flex flex-fill justify-content-between">
                <Button
                  className={`btn ${selectedPaymentMethod === 1 ? "btn-success" : "btn-light"} flex-grow-1 mr-1`}
                  onClick={() => {
                    setSelectedPaymentMethod(1);

                  }}
                >
                  ชำระด้วยเงินสด
                </Button>
                &nbsp;&nbsp;
                <Button
                  className={`btn ${selectedPaymentMethod === 2 ? "btn-success" : "btn-light"} flex-grow-1 ml-1`}
                  onClick={() => {
                    setSelectedPaymentMethod(2);
                    qr_code();
                  }}
                >
                  ชำระด้วย QR CODE
                </Button>
              </div>
              <img id="qrImage" src={pic_qr_code} alt="PromptPay QR Code"></img>
              <p>เจ้าของ QR Code: {ownerName}</p>
              <p>ยอดเงิน: {amount} บาท</p>
            </Modal.Body>
          </Modal>



        </main>
      </body>
    </>
  )

}

export default Dashboard
