import React, { useState, useEffect } from 'react'
import Navbar from "../Navbar";
import axios from 'axios';
function Customer() {
    const [showModal, setShowModal] = useState(false);
    const [cus, setUsers] = useState([]);
    const [name, setName] = useState("");
    const [roomType, setRoomType] = useState("");
    const [roomNumber, setRoomNumber] = useState("");
    const [status, setStatus] = useState("");
    const [checkInDate, setCheckInDate] = useState("");
    const urlserver = "http://localhost:4000";
    //const urlserver = "https://nodejs-ktey.onrender.com";
    useEffect(() => {
        fetchUsers();
      }, []);

    const fetchUsers = async () => {
        try {
            const response = await axios.get(urlserver + `/api_customer`);
            setUsers(response.data);
            console.log(response.data);
        } catch (error) {
            console.error(error);
        }
    };
    const ob_status = {
        1: 'พักอาศัย',
        2: 'ออก',
    };
    const openModal = () => {
        setShowModal(true);
        console.log('');
    };
    const closeModal = () => {
        setShowModal(false);
    };
    const handleSignup = (e) => {
        e.preventDefault();
        // เขียนโค้ดส่วนที่ต้องการทำเมื่อกด submit ฟอร์มเพิ่มข้อมูลที่ Modal
      };
    return (
        <>
            <body class="g-sidenav-show   bg-gray-100">
                <Navbar />
                <main class="main-content position-relative border-radius-lg ">
                    {/* table */}
                    <br />
                    <div align="right" class=" container-fluid ">
                        <button type="button" className="btn btn-success" onClick={openModal} >
                            เพิ่มข้อมูล
                        </button>
                    </div>
                    <div class="container-fluid py2">
                        <div class="row">


                            <div class="col-12">
                                <div class="card mb-4">


                                    <div class="card-header pb-0">
                                        <h6>ค่าเช่าที่ต้องเก็บ</h6>
                                    </div>
                                    <div class="card-body px-0 pt-0 pb-2">
                                        <div class="table-responsive p-0">
                                            <table class="table align-items-center mb-0">
                                                <thead>
                                                    <tr>
                                                        <th class="text-center  text-secondary text-xs font-weight-bolder opacity-7">ชื่อ</th>
                                                        <th class="text-center  text-secondary text-xs font-weight-bolder opacity-7 ps-2">ห้องที่/ชนิดห้อง</th>
                                                        <th class="text-center  text-secondary text-xs font-weight-bolder opacity-7">สถานะ</th>
                                                        <th class="text-center opacity-7">#</th>
                                                    </tr>
                                                </thead>
                                                <tbody>
                                                    {cus.map((file, index) => (
                                                        <tr key={index}>
                                                            <td class="text-center  text-sm font-weight-bolder opacity-8">
                                                                {file.cus_name}
                                                            </td>
                                                            <td class="text-center text-sm font-weight-bolder opacity-8">
                                                                {file.cus_room + " " + file.cus_typeroom}
                                                            </td>
                                                            <td class="text-center text-sm font-weight-bolder opacity-8">
                                                                <span class="badge badge-sm bg-gradient-success">{ob_status[file.cus_room]}</span>
                                                            </td>
                                                            <td class="text-center text-sm font-weight-bolder opacity-8"></td>
                                                        </tr>
                                                    ))}
                                                </tbody>

                                            </table>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>


          {/* Modal */}
          {showModal && (
            <div className="modal">
              <div className="modal-dialog">
                <div className="modal-content">
                  <div className="modal-header">
                    <h5 className="modal-title">เพิ่มข้อมูล</h5>
                    <button type="button" className="close" onClick={closeModal}>
                      <span aria-hidden="true">&times;</span>
                    </button>
                  </div>
                  <div className="modal-body">
                    <form onSubmit={handleSignup}>
                      <div className="form-group">
                        <label>Name</label>
                        <input
                          type="text"
                          className="form-control"
                          value={name}
                          onChange={(e) => setName(e.target.value)}
                        />
                      </div>
                      <div className="form-group">
                        <label>ชนิดห้อง</label>
                        <select
                          className="form-control"
                          value={roomType}
                          onChange={(e) => setRoomType(e.target.value)}
                        >
                          <option value="">-- เลือกชนิดห้อง --</option>
                          <option value="1">ห้องไม้</option>
                          <option value="2">ห้องปูน</option>
                        </select>
                      </div>
                      <div className="form-group">
                        <label>เลขห้อง</label>
                        <input
                          type="text"
                          className="form-control"
                          value={roomNumber}
                          onChange={(e) => setRoomNumber(e.target.value)}
                        />
                      </div>
                      <div className="form-group">
                        <label>สถานะ</label>
                        <input
                          type="text"
                          className="form-control"
                          value={status}
                          onChange={(e) => setStatus(e.target.value)}
                        />
                      </div>
                      <div className="form-group">
                        <label>วันที่เข้าพัก</label>
                        <input
                          type="date"
                          className="form-control"
                          value={checkInDate}
                          onChange={(e) => setCheckInDate(e.target.value)}
                        />
                      </div>
                      <div className="modal-footer">
                        <button type="submit" className="btn btn-primary">
                          ยืนยัน
                        </button>
                        <button type="button" className="btn btn-secondary" onClick={closeModal}>
                          ปิด
                        </button>
                      </div>
                    </form>
                  </div>
                </div>
              </div>
            </div>
          )}
                </main>
            </body>
        </>
    )
}

export default Customer
