import React, { useState, useEffect } from 'react'
import { Button, Modal, Form } from 'react-bootstrap';
import Navbar from "../Navbar";
import Indexmain from '../indexmain';
import axios from 'axios';
import Swal from 'sweetalert2';
import { useNavigate } from 'react-router-dom'; // Import useNavigate
const Customers = () => {
  const navigate = useNavigate(); // Initialize useNavigate
  const usersession = JSON.parse(sessionStorage.getItem('user'));
  if (usersession === null || usersession.user_status === "user") {
    setTimeout(() => {
      Swal.fire({
        icon: 'error',
        title: 'คุณไม่มีสิทธิ์เข้า',
        text: 'กรุณาล็อกอินใหม่อีกครั้ง',
      });
      navigate('/main_login');
    }, 1000);
  }

  const [showModal, setShowModal] = useState(false);
  const [showModaledit, setShowModaledit] = useState(false);
  const [users, setUsers] = useState([]);
  const [user_id, setuser_id] = useState();
  const [room_typename, setroom_typename] = useState("");
  const [room_name, setroom_name] = useState("");
  const [room_status, setroom_status] = useState("");

  const [user_username, set_user_username] = useState("");
  const [user_password, set_user_password] = useState("");
  const [user_status, set_user_status] = useState("admin");
  const [user_usage, set_user_usage] = useState("1");



  const [edit_room_guarantee, set_edit_room_guarantee] = useState("");
  const [edit_room_advance, set_edit_room_advance] = useState("");
  const [edit_room_bin_price, set_edit_room_bin_price] = useState("");
  const [edit_room_water, set_edit_room_water] = useState("");
  const [edit_room_electricity, set_edit_room_electricity] = useState("");
  const [edit_user_id, set_edit_user_id] = useState();
  const [edit_room_typename, set_edit_room_typename] = useState("");
  const [edit_room_name, set_edit_room_name] = useState("");
  const [edit_room_status, set_edit_room_status] = useState("");
  const [edit_room_price, set_edit_room_price] = useState("");
  const [edit_room_meter_water, set_edit_room_meter_water] = useState("");
  const [edit_room_meter_electricity, set_edit_room_meter_electricity] = useState("");

  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  const urlserver = "http://localhost:4000";
  useEffect(() => {
    return () => {
      fetchUsers();
    }
  }, []);


  //    const urlserver = "https://homerentalbackend.onrender.com";
  //     useEffect(() => {
  //             fetchUsers();
  //     }, []);

  const startIndex = (currentPage - 1) * 10;
  const endIndex = currentPage * 10;
  const currentUsers = users.slice(startIndex, endIndex);
  const changePage = (page) => {
    setCurrentPage(page);
  };
  const fetchUsers = async () => {
    try {
      const response = await axios.get(urlserver + `/api_user`);
      setUsers(response.data);

      const num = response.data.length
      if (num > 0) {
        const fecth_user_id = await axios.get(urlserver + `/api_user?user_id=fetch_user_id_last`);
        const user_id_numlast = fecth_user_id.data[0].user_id;
        setuser_id(user_id_numlast + 1);
      } else {
        setuser_id(1);
      }
      setTotalPages(Math.ceil(response.data.length / 10));
      // const currentDate = new Date();
      // const formattedDate = currentDate.toISOString().split('T')[0];
      set_user_username("");
      set_user_password("");
      set_user_status(1);


    } catch (error) {
      console.error(error);
    }
  };

  const fetchedit = async (id) => {
    try {
      const response = await axios.get(urlserver + `/api_room/edit?user_id=${id}`);

      set_edit_user_id(response.data[0].user_id);
      // set_edit_user_username(response.data[0].user_username);
      // set_edit_user_password(response.data[0].user_password);
      // set_edit_user_status(response.data[0].user_status);
      // set_edit_user_usage(response.data[0].user_usage);

    } catch (error) {
      console.error(error);
    }
  };
  const deleterow = async (id) => {

    try {
      const response = await axios.delete(urlserver + `/api_room/delete?user_id=${id}`);
      console.log(response.data); // ตัวอย่างการใช้งาน response ที่ได้จาก server

      if (response.data !== "") {
        Swal.fire({
          title: '',
          text: 'ลบข้อมูลสำเร็จ',
          icon: 'success',
        });
        fetchUsers();
      }

    } catch (error) {
      console.error(error);
    }
  };
  const ob_status = {
    1: 'ใช้งาน',
    2: 'ไม่ใช้งาน',
  };

  const openModal = () => {
    setShowModal(true);

  };
  const closeModal = () => {
    setShowModal(false);
  };
  const openModaledit = (id) => {


    fetchedit(id)
    setShowModaledit(true);
  };
  const closeModaledit = () => {
    setShowModaledit(false);
  };
  const handleSignup = async (e) => {
    e.preventDefault();
    try {

      const response = await axios.post(urlserver + `/api_room/create`, {
        user_id,
        user_username,
        user_password,
        user_status,
        user_usage
      });
      // กระบวนการอื่นๆ เมื่อสำเร็จ
      console.log(response.data); // ตัวอย่างการใช้งาน response ที่ได้จาก server
      if (response.data !== "") {
        Swal.fire({
          title: '',
          text: 'บันทึกข้อมูลสำเร็จ',
          icon: 'success',
        });
        setShowModal(false);
      }
      fetchUsers();
    } catch (error) {
      console.error(error);
    }
  };
  const updatmodal = async (e) => {
    e.preventDefault();
    try {
      if (edit_room_name === "") {
        Swal.fire({
          title: '',
          text: 'กรุณากรอก เลขห้อง',
          icon: 'warning',
        });
        return false;
      } else if (edit_room_typename === "") {
        Swal.fire({
          title: '',
          text: 'กรุณากรอก ประเภทห้อง',
          icon: 'warning',
        });
        return false;

      } else if (edit_room_bin_price === "") {
        Swal.fire({
          title: '',
          text: 'กรุณากรอก ค่าขยะ',
          icon: 'warning',
        });
        return false;
      } else if (edit_room_price === "") {
        Swal.fire({
          title: '',
          text: 'กรุณากรอก ราคาห้อง',
          icon: 'warning',
        });
        return false;

      } else if (edit_room_guarantee === "") {
        Swal.fire({
          title: '',
          text: 'กรุณากรอก ค่าประกัน',
          icon: 'warning',
        });
        return false;

      } else if (edit_room_advance === "") {
        Swal.fire({
          title: '',
          text: 'กรุณากรอก ค่ามัดจำล่วงหน้า',
          icon: 'warning',
        });
        return false;

      } else if (edit_room_water === "") {
        Swal.fire({
          title: '',
          text: 'กรุณากรอก ค่าน้ำ',
          icon: 'warning',
        });
        return false;

      } else if (edit_room_electricity === "") {
        Swal.fire({
          title: '',
          text: 'กรุณากรอก ค่าไฟฟ้า',
          icon: 'warning',
        });
        return false;
      }
      const response = await axios.put(urlserver + `/api_room/update`, {
        edit_user_id,
        edit_room_name,
        edit_room_typename,
        edit_room_bin_price,
        edit_room_price,
        edit_room_guarantee,
        edit_room_advance,
        edit_room_electricity,
        edit_room_water,
        edit_room_status,
        edit_room_meter_water,
        edit_room_meter_electricity,
      });
      // กระบวนการอื่นๆ เมื่อสำเร็จ

      console.log(response.data); // ตัวอย่างการใช้งาน response ที่ได้จาก server
      if (response.data !== "") {
        Swal.fire({
          title: '',
          text: 'แก้ไขข้อมูลสำเร็จ',
          icon: 'success',
        });
        setShowModaledit(false);
      }
      fetchUsers();
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <>
      <Indexmain />
      <body className="g-sidenav-show   bg-gray-100">
        <Navbar />
        <main className="main-content position-relative border-radius-lg ">
          {/* table */}
          <br />
          <div align="right" className=" container-fluid ">
            <button type="button" className="btn btn-success" onClick={openModal} >
              เพิ่มข้อมูลผู้ใช้
            </button>
          </div>
          <div className="container-fluid py2">
            <div className="row">
              <div className="col-12">
                <div className="card mb-4">
                  <div className="card-header pb-0">
                    <h6>ข้อมูลผู้ใช้งาน</h6>
                  </div>
                  <div className="card-body px-0 pt-0 pb-2">
                    <div className="table-responsive p-0">
                      <table className="table align-items-center mb-0">
                        <thead>
                          <tr>
                            <th className="text-center  text-secondary text-xs font-weight-bolder opacity-7">ผู้ใช้งาน</th>
                            <th className="text-center  text-secondary text-xs font-weight-bolder opacity-7 ps-2">รหัสผ่าน</th>
                            <th className="text-center  text-secondary text-xs font-weight-bolder opacity-7">สิทธิ์</th>
                            <th className="text-center  text-secondary text-xs font-weight-bolder opacity-7">สถานะ</th>
                            <th colSpan={2} className="text-center opacity-7">#</th>
                          </tr>
                        </thead>
                        <tbody>
                          {currentUsers.map((file, index) => (
                            <tr key={index}>
                              <td className="text-center  text-sm font-weight-bolder opacity-8">
                                {file.user_username}
                              </td>
                              <td className="text-center text-sm font-weight-bolder opacity-8">
                                {file.user_password}
                              </td>
                              {file.user_status === "admin" ? (
                                <td className="text-center ">
                                  <span style={{ width: '70px' }} className="badge badge-sm bg-gradient-info">{file.user_status}</span>
                                </td>
                              ) : (
                                <td className="text-center ">
                                  <span style={{ width: '70px' }} className="badge badge-sm bg-gradient-warning">{file.user_status}</span>
                                </td>
                              )}
                              {file.user_usage === 1 ? (
                                <td className="text-center ">
                                  <span style={{ width: '70px' }} className="badge badge-sm bg-gradient-success">{ob_status[file.user_usage]}</span>
                                </td>
                              ) : (
                                <td className="text-center ">
                                  <span style={{ width: '70px' }} className="badge badge-sm bg-gradient-danger">{ob_status[file.user_usage]}</span>
                                </td>
                              )}
                              <td className="text-center ">
                                <button className="btn btn-primary" type='button' onClick={() => openModaledit(file.user_id)}>
                                  <i className="fa-regular fa-pen-to-square"></i>
                                </button>
                                &nbsp;
                                <button className="btn btn-danger" type='button' onClick={() => deleterow(file.user_id)}>
                                  <i className="fa-solid fa-trash-can"></i>
                                </button>
                              </td>
                            </tr>
                          ))}
                        </tbody>
                      </table>
                    </div>
                  </div>
                  <ul className="pagination justify-content-end">
                    {Array.from({ length: totalPages }, (_, i) => i + 1).map((page) => (
                      <li className={`page-item ${currentPage === page ? 'active' : ''}`}
                        key={page}
                        onClick={() => changePage(page)}>
                        <span className="page-link">{page}</span>
                      </li>
                    ))}
                  </ul>

                </div>
              </div>
            </div>
          </div>
        </main>
      </body >

      {/* <add> */}
      <Modal show={showModal} onHide={closeModal}>
        <Modal.Header closeButton>
          <Modal.Title>เพิ่มข้อมูลผู้ใช้</Modal.Title>
        </Modal.Header>
        <Modal.Body >
          <Form onSubmit={handleSignup}>
            <Form.Group >
              <Form.Label>user_id</Form.Label>
              <Form.Control
                type="text"
                value={user_id}
                onChange={(e) => setuser_id(e.target.value)}
              />
            </Form.Group>
            <Form.Group>
              <Form.Label><span style={{ color: 'red' }}>*</span> ชื่อผู้ใช้</Form.Label>
              <Form.Control
                type="text"
                value={user_username}
                onChange={(e) => set_user_username(e.target.value)}
              />
            </Form.Group>
            <Form.Group>
              <Form.Label><span style={{ color: 'red' }}>*</span> รหัสผ่าน</Form.Label>
              <Form.Control
                type="text"
                value={user_password}
                onChange={(e) => set_user_password(e.target.value)}
              />
            </Form.Group>
            <Form.Group>
              <Form.Label><span style={{ color: 'red' }}>*</span> สิทธิ์</Form.Label>
              <Form.Control
                as="select"
                value={user_status}
                onChange={(e) => set_user_status(e.target.value)}>
                <option value="admin" >admin</option>
                <option value="user">user</option>

              </Form.Control>

            </Form.Group>

            <Form.Group >
              <Form.Label>สถานะ</Form.Label>
              <Form.Control
                as="select"
                value={user_usage}
                onChange={(e) => set_user_usage(e.target.value)} readOnly>
 

              </Form.Control>
            </Form.Group>

            <Modal.Footer>
              <Button variant="success" type="submit" >
                ยืนยัน
              </Button>
              <Button variant="danger" onClick={closeModal}>
                ปิด
              </Button>
            </Modal.Footer>
          </Form>
        </Modal.Body>
      </Modal>

      {/* <edit> */}
      <Modal show={showModaledit} onHide={closeModal}>
        <Modal.Header closeButton>
          <Modal.Title>แก้ไขข้อมูล</Modal.Title>
        </Modal.Header>
        <Modal.Body >
          <Form onSubmit={updatmodal}>
            <Form.Group hidden >
              <Form.Label>user_id</Form.Label>
              <Form.Control
                type="text"
                value={edit_user_id}
                onChange={(e) => set_edit_user_id(e.target.value)}
              />
            </Form.Group>
            <Form.Group>
              <Form.Label><span style={{ color: 'red' }}>*</span> ประเภทห้อง</Form.Label>
              <Form.Control
                type="text"
                value={edit_room_typename}
                onChange={(e) => set_edit_room_typename(e.target.value)}
              />
            </Form.Group>

            <Form.Group>
              <Form.Label><span style={{ color: 'red' }}>*</span> เลขห้อง</Form.Label>
              <Form.Control
                type="text"
                value={edit_room_name}
                onChange={(e) => set_edit_room_name(e.target.value)}
              />
            </Form.Group>
            <div style={{ display: 'flex', alignItems: 'center' }}>
              <div style={{ flex: '1', marginRight: '10px' }}>
                <Form.Group>
                  <Form.Label><span style={{ color: 'red' }}>*</span> ค่าห้อง</Form.Label>
                  <Form.Control
                    type="text"
                    value={edit_room_price}
                    onChange={(e) => {
                      const inputValue = e.target.value.replace(/\D/g, ''); // เอาแต่ตัวเลข
                      const formattedValue = Number(inputValue).toLocaleString('en-US');
                      set_edit_room_price(formattedValue);

                    }}
                  />
                </Form.Group>
              </div>
              <div style={{ flex: '1', marginRight: '10px' }}>
                <Form.Group>
                  <Form.Label><span style={{ color: 'red' }}>*</span> ค่ามัดจำล่วงหน้า</Form.Label>
                  <Form.Control
                    type="text"
                    value={edit_room_advance}
                    onChange={(e) => {
                      const inputValue = e.target.value.replace(/\D/g, ''); // เอาแต่ตัวเลข
                      const formattedValue = Number(inputValue).toLocaleString('en-US');
                      set_edit_room_advance(formattedValue);

                    }}
                  />
                </Form.Group>

              </div>
            </div>
            <Form.Group>
              <Form.Label><span style={{ color: 'red' }}>*</span> ค่าประกัน</Form.Label>
              <Form.Control
                type="text"
                value={edit_room_guarantee}
                onChange={(e) => {
                  const inputValue = e.target.value.replace(/\D/g, ''); // เอาแต่ตัวเลข
                  const formattedValue = Number(inputValue).toLocaleString('en-US');
                  set_edit_room_guarantee(formattedValue);

                }}
              />
            </Form.Group>
            <div style={{ display: 'flex', alignItems: 'center' }}>
              <div style={{ flex: '1', marginRight: '10px' }}>
                <Form.Group>
                  <Form.Label><span style={{ color: 'red' }}>*</span> ค่าน้ำ</Form.Label>
                  <Form.Control
                    type="text"
                    value={edit_room_water}
                    onChange={(e) => {
                      const inputValue = e.target.value.replace(/\D/g, ''); // เอาแต่ตัวเลข
                      const formattedValue = Number(inputValue).toLocaleString('en-US');
                      set_edit_room_water(formattedValue);

                    }}
                  />
                </Form.Group>
              </div>
              <div style={{ flex: '1', marginRight: '10px' }}>
                <Form.Group>
                  <Form.Label><span style={{ color: 'red' }}>*</span> มิตเตอร์น้ำ</Form.Label>
                  <Form.Control
                    type="text"
                    value={edit_room_meter_water}
                    onChange={(e) => {
                      const inputValue = e.target.value.replace(/\D/g, ''); // เอาแต่ตัวเลข
                      const formattedValue = Number(inputValue).toLocaleString('en-US');
                      set_edit_room_meter_water(formattedValue);

                    }}
                  />
                </Form.Group>
              </div>
            </div>
            <div style={{ display: 'flex', alignItems: 'center' }}>
              <div style={{ flex: '1', marginRight: '10px' }}>
                <Form.Group>
                  <Form.Label><span style={{ color: 'red' }}>*</span> ค่าไฟฟ้า</Form.Label>
                  <Form.Control
                    type="text"
                    value={edit_room_electricity}
                    onChange={(e) => {
                      const inputValue = e.target.value.replace(/\D/g, ''); // เอาแต่ตัวเลข
                      const formattedValue = Number(inputValue).toLocaleString('en-US');
                      set_edit_room_electricity(formattedValue);

                    }}
                  />
                </Form.Group>
              </div>
              <div style={{ flex: '1', marginRight: '10px' }}>
                <Form.Group>
                  <Form.Label><span style={{ color: 'red' }}>*</span> มิตเตอร์ไฟฟ้า</Form.Label>
                  <Form.Control
                    type="text"
                    value={edit_room_meter_electricity}
                    onChange={(e) => {
                      const inputValue = e.target.value.replace(/\D/g, ''); // เอาแต่ตัวเลข
                      const formattedValue = Number(inputValue).toLocaleString('en-US');
                      set_edit_room_meter_electricity(formattedValue);

                    }}
                  />
                </Form.Group>
              </div>
            </div>
            <div style={{ display: 'flex', alignItems: 'center' }}>
              <div style={{ flex: '1', marginRight: '10px' }}>
                <Form.Group>
                  <Form.Label><span style={{ color: 'red' }}>*</span> ค่าขยะ</Form.Label>
                  <Form.Control
                    type="text"
                    value={edit_room_bin_price}
                    onChange={(e) => {
                      const inputValue = e.target.value.replace(/\D/g, ''); // เอาแต่ตัวเลข
                      const formattedValue = Number(inputValue).toLocaleString('en-US');
                      set_edit_room_bin_price(formattedValue);

                    }}
                  />
                </Form.Group>
              </div>
            </div>

            <Form.Group>
              <Form.Label>สถานะ</Form.Label>
              <Form.Control
                as="select"
                value={edit_room_status}
                onChange={(e) => set_edit_room_status(e.target.value)}>
                <option value="1" >ว่าง</option>
                <option value="2">ไม่ว่าง</option>

              </Form.Control>
            </Form.Group>

            <Modal.Footer>
              <Button variant="success" type="submit" >
                ยืนยัน
              </Button>
              <Button variant="danger" onClick={closeModaledit}>
                ปิด
              </Button>
            </Modal.Footer>
          </Form>
        </Modal.Body>
      </Modal>
    </>
  )
};

export default Customers
