import React, { useState, useEffect } from 'react'
import Navbar from "../Navbar";
import axios from 'axios';
import { Button, Modal , Form } from 'react-bootstrap';
function Dashboard() {
  const [selectedPaymentMethod, setSelectedPaymentMethod] = useState(1);
  const [pic_qr_code, set_pic_qr_code] = useState(1);
  const [ownerName, set_ownerName] = useState(1);
  const [amount, set_amount] = useState(1);
  const [pay_id, set_pay_id ] = useState("");
  const [pay_cus_name, set_pay_cus_name] = useState("");
  const [pay_cus_room_id, set_pay_cus_room_id] = useState("");
  const [pay_cus_room_price, set_pay_cus_room_price] = useState("");
  const [pay_meter_water, set_pay_meter_water] = useState("");
  const [pay_price_water, set_pay_price_water] = useState("");
  const [pay_meter_electricity, set_pay_meter_electricity] = useState("");
  const [pay_price_electricity, set_pay_price_electricity] = useState("");
  const [pay_room_bin_price, set_pay_room_bin_price] = useState("");
  const [pay_price_total, set_pay_price_total] = useState("");
  const [pay_room_meter_water, set_pay_room_meter_water] = useState("");
  const [pay_room_meter_electricity, set_pay_room_meter_electricity] = useState("");

  const [data, setData] = useState([]);
  // const [showModal, setShowModal] = useState(false);
  const [showModaledit, setShowModaledit] = useState(false);
  const [showqr, setShowshowqr] = useState(false);
  const urlserver = "http://localhost:4000";
  //const urlserver = "https://backendrental.onrender.com";
  useEffect(() => {
    return () => {
      fetchUsers();

    }
  }, []);


  const fetchUsers = async () => {
    try {
      await axios.post(urlserver + `/api_payment/create`);
      const response = await axios.get(urlserver + `/api_payment`);
      setData(response.data);
      console.log(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const fetchedit = async (id) => {
    try {
        const response = await axios.get(urlserver + `/api_customer/edit?cus_id=${id}`);
        console.log(response.data);
        set_edit_cus_id(response.data[0].cus_id);
        set_edit_cus_name(response.data[0].cus_name);
        set_edit_cus_status(response.data[0].cus_status);
        set_edit_cus_datein(response.data[0].cus_datein);
        set_edit_cus_room_id(response.data[0].cus_room_id);
        set_edit_cus_room_price(response.data[0].cus_room_price.toLocaleString());
        set_edit_cus_room_guarantee(response.data[0].cus_room_guarantee.toLocaleString());
        set_edit_cus_room_advance(response.data[0].cus_room_advance.toLocaleString());
        set_edit_cus_room_water(response.data[0].cus_room_water.toLocaleString());
        set_edit_cus_room_electricity(response.data[0].cus_room_electricity.toLocaleString());
        set_edit_cus_room_bin_price(response.data[0].cus_room_bin_price.toLocaleString());
        set_edit_cus_room_sum(response.data[0].cus_room_sum.toLocaleString());
        setShowModaledit(true);
    } catch (error) {
        console.error(error);
    }
};
  const deleteRow = async (id) => {
  };
  const pay_room = (id) => {
    fetchedit(id);

  };
  const closeModal = () => {
    setShowModaledit(false);
  };
  const qr_code = async () => {

    try {
      const response = await axios.get(urlserver + `/api_payment/qr_code`);

      set_pic_qr_code(response.data.qrData);
      set_ownerName(response.data.ownerName);
      set_amount(response.data.amount.toLocaleString('th-TH', {
        style: 'currency',
        currency: 'THB',
        minimumFractionDigits: 2
      }));
      setShowshowqr(true);
    } catch (error) {
      console.error(error);
    }
  };
  const handleSignup = async (e) => {
    e.preventDefault();
 
};
  const noqr_code = async () => {

    setShowshowqr(false);
  };
  return (
    <>
      <body className="g-sidenav-show   bg-gray-100">
        <Navbar />
        <main className="main-content position-relative border-radius-lg ">

          <div className="container-fluid py-4 ">
            <div className="row">
              <div className="col-xl-3 col-sm-6 mb-xl-0 mb-4">
                <div className="card">
                  <div className="card-body p-3">
                    <div className="row">
                      <div className="col-8">
                        <div className="numbers">
                          <p className="text-sm mb-0 text-uppercase font-weight-bold">ยอดที่ค้างชำระทั้งหมด</p>
                          <h5 className="font-weight-bolder">
                            <span className="text-danger text-m font-weight-bolder">5,000 </span>
                            บาท

                          </h5>

                          <p className="mb-0">

                          </p>
                        </div>
                      </div>
                      <div className="col-4 text-end">
                        <div className="icon icon-shape bg-gradient-danger shadow-primary text-center rounded-circle">
                          <i className="ni ni-money-coins text-lg opacity-10" aria-hidden="true"></i>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
              <div className="col-xl-3 col-sm-6 mb-xl-0 mb-4">
                <div className="card">
                  <div className="card-body p-3">
                    <div className="row">
                      <div className="col-8">
                        <div className="numbers">
                          <p className="text-sm mb-0 text-uppercase font-weight-bold">ยอดที่ได้รับทั้งสิ้น</p>
                          <h5 className="font-weight-bolder">
                            <span className="text-success text-m font-weight-bolder">10,000 </span>
                            บาท
                          </h5>
                          <p className="mb-0">

                          </p><i class="bi bi-cash-coin"></i>
                        </div>
                      </div>
                      <div className="col-4 text-end">
                        <div className="icon icon-shape bg-gradient-success shadow-danger text-center rounded-circle">
                          <i className="ni ni-money-coins text-lg opacity-10" aria-hidden="true"></i>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>

            </div>
          </div>

          {/* table */}
          <div className="container-fluid py-4">
            <div className="row">
              <div className="col-12">
                <div className="card mb-4">
                  <div className="card-header pb-0">
                    <h6>ค่าเช่าที่ต้องเก็บ</h6>
                  </div>
                  <div className="card-body px-0 pt-0 pb-2">
                    <div className="table-responsive p-0">
                      <table className="table align-items-center mb-0">
                        <thead>
                          <tr>
                            <th className="text-center  text-secondary text-xs font-weight-bolder opacity-7">ชื่อ<br />เลขห้อง<br />ประเภทห้อง</th>
                            <th className="text-center  text-secondary text-xs font-weight-bolder opacity-7">ยอดที่<br />ต้องชำระ</th>
                            <th className="text-center  text-secondary text-xs font-weight-bolder opacity-7">งวด<br />เดือน/ปี</th>
                            <th className="text-center opacity-7">#</th>
                          </tr>
                        </thead>
                        <tbody>
                          {data.map((data, index) => (
                            <tr key={index}>
                              <td className="text-center  text-xs font-weight-bolder opacity-8">
                                {data.pay_cus_name}<br />{data.roomData.room_name + ' ' + data.roomData.room_typename}
                              </td>
                              <td className="text-center text-xs font-weight-bolder opacity-8">
                                {data.pay_cus_room_sum.toLocaleString()}
                              </td>
                              <td className="text-center text-xs font-weight-bolder opacity-8">
                                {data.pay_round}
                              </td>
                              <td className="text-center ">
                                <button className="btn btn-info" type='button' onClick={() => pay_room(data.pay_id)}>
                                  <i className="fa-regular fa-pen-to-square"></i>
                                </button>
                                &nbsp;
                                <button className="btn btn-danger" type='button' onClick={() => deleteRow(data.pay_id)}>
                                  <i className="fa-solid fa-trash-can"></i>
                                </button>
                              </td>
                            </tr>
                          ))}
                        </tbody>

                      </table>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
          {/* <add> */}
          <Modal show={showModaledit} onHide={closeModal}>
            <Modal.Header closeButton>
              <Modal.Title>ชำระเงิน</Modal.Title>
            </Modal.Header>
            <Modal.Body className="d-flex flex-column">

              <Form onSubmit={handleSignup}>
                <Form.Group  >
                  <Form.Label>pay_id</Form.Label>
                  <Form.Control
                    type="text"
                    value={pay_id}
                    onChange={(e) => set_pay_id(e.target.value)} />
                </Form.Group>

                <Form.Group >
                  <Form.Label ><span style={{ color: 'red' }}>*</span> ชื่อ-นามสกุล</Form.Label>
                  <Form.Control
                    type="text"
                    value={pay_cus_name}
                    onChange={(e) => set_pay_cus_name(e.target.value)}
                  />
                </Form.Group>

                <div style={{ display: 'flex', alignItems: 'center' }}>
                  <div style={{ flex: '1', marginRight: '10px' }}>
                    <Form.Group>
                      <Form.Label><span style={{ color: 'red' }}>*</span>ประเภทห้อง/เลขห้อง</Form.Label>
                      <Form.Control
                        type="text"
                        value={pay_cus_room_id}
                        onChange={(e) => {
                          const inputValue = e.target.value.replace(/\D/g, ''); // เอาแต่ตัวเลข
                          const formattedValue = Number(inputValue).toLocaleString('en-US');
                          set_pay_cus_room_id(formattedValue);


                        }}
                      />
                    </Form.Group>
                  </div>
                  </div>
                  <div style={{ flex: '1', marginRight: '10px' }}>
                    <Form.Group>
                      <Form.Label><span style={{ color: 'red' }}>*</span> ค่าห้อง</Form.Label>
                      <Form.Control
                        type="text"
                        value={pay_cus_room_price}
                        onChange={(e) => {
                          const inputValue = e.target.value.replace(/\D/g, ''); // เอาแต่ตัวเลข
                          const formattedValue = Number(inputValue).toLocaleString('en-US');
                          set_pay_cus_room_price(formattedValue);
                         
                        }}
                      />
                    </Form.Group>
                  </div>
             

                <div style={{ display: 'flex', alignItems: 'center' }} >
                  <div style={{ flex: '1', marginRight: '10px' }}>
                    <Form.Group>
                      <Form.Label><span style={{ color: 'red' }}>*</span> มิตเตอร์น้ำเดือนก่อน</Form.Label>
                      <Form.Control
                        type="text"
                        value={pay_room_meter_water}
                        onChange={(e) => {
                          const inputValue = e.target.value.replace(/\D/g, '');
                          const formattedValue = Number(inputValue).toLocaleString('en-US');
                          set_pay_room_meter_water(formattedValue);
                        }}
                      />
                        </Form.Group>
                      </div>
                      <div style={{ flex: '1', marginRight: '10px' }}> 
           
                    <Form.Group>
                      <Form.Label><span style={{ color: 'red' }}>*</span> มิตเตอร์น้ำล่าสุด</Form.Label>
                      <Form.Control
                        type="text"
                        value={pay_meter_water}
                        onChange={(e) => {
                          const inputValue = e.target.value.replace(/\D/g, '');
                          const formattedValue = Number(inputValue).toLocaleString('en-US');
                          set_pay_meter_water(formattedValue);
                        }}
                      />
                    </Form.Group>
                  </div>
                  <div style={{ flex: '1', marginLeft: '10px' }}>
                    <Form.Group>
                      <Form.Label><span style={{ color: 'red' }}>*</span> ค่าน้ำ</Form.Label>
                      <Form.Control
                        type="text"
                        value={pay_price_water}
                        onChange={(e) => {
                          const inputValue = e.target.value.replace(/\D/g, '');
                          const formattedValue = Number(inputValue).toLocaleString('en-US');
                          set_pay_price_water(formattedValue);
                        }}
                      />  
                    </Form.Group>
                  </div>
                </div>
                <div style={{ display: 'flex', alignItems: 'center' }} >
                  <div style={{ flex: '1', marginRight: '10px' }}>
                    <Form.Group>
                      <Form.Label><span style={{ color: 'red' }}>*</span> มิตเตอร์ไฟฟ้าเดือนก่อน</Form.Label>
                      <Form.Control
                        type="text"
                        value={pay_room_meter_electricity}
                        onChange={(e) => {
                          const inputValue = e.target.value.replace(/\D/g, '');
                          const formattedValue = Number(inputValue).toLocaleString('en-US');
                          set_pay_room_meter_electricity(formattedValue);
                        }}
                      />
                    </Form.Group>
                  </div>
                  <div style={{ flex: '1', marginRight: '10px' }}>
                    <Form.Group>
                      <Form.Label><span style={{ color: 'red' }}>*</span> มิตเตอร์ไฟฟ้าล่าสุด</Form.Label>
                      <Form.Control
                        type="text"
                        value={pay_meter_electricity}
                        onChange={(e) => {
                          const inputValue = e.target.value.replace(/\D/g, '');
                          const formattedValue = Number(inputValue).toLocaleString('en-US');
                          set_pay_meter_electricity(formattedValue);
                        }}
                      />
                    </Form.Group>
                  </div>
                  <div style={{ flex: '1', marginLeft: '10px' }}>
                    <Form.Group>
                      <Form.Label><span style={{ color: 'red' }}>*</span> ค่าไฟฟ้า</Form.Label>
                      <Form.Control
                        type="text"
                        value={pay_price_electricity}
                        onChange={(e) => {
                          const inputValue = e.target.value.replace(/\D/g, '');
                          const formattedValue = Number(inputValue).toLocaleString('en-US');
                          set_pay_price_electricity(formattedValue);
                        }}
                      />
                    </Form.Group>
                  </div>
                  </div>
                  <div style={{ flex: '1', marginLeft: '10px' }}>
                    <Form.Group>
                      <Form.Label><span style={{ color: 'red' }}>*</span> ค่าขยะ</Form.Label>
                      <Form.Control
                        type="text"
                        value={pay_room_bin_price}
                        onChange={(e) => {
                          const inputValue = e.target.value.replace(/\D/g, ''); // เอาแต่ตัวเลข
                          const formattedValue = Number(inputValue).toLocaleString('en-US');
                          set_pay_room_bin_price(formattedValue);
                        }}
                      />
                    </Form.Group>
                
                </div>

                <Form.Group  >
                  <Form.Label><span style={{ color: 'red' }}>*</span> ยอดที่ต้องชำระ</Form.Label>
                  <Form.Control style={{ backgroundColor: '#42bd41', color: 'white' }}
                    type="text"
                    value={pay_price_total}
                    onChange={(e) => {
                      const inputValue = e.target.value.replace(/\D/g, ''); // เอาแต่ตัวเลข
                      const formattedValue = Number(inputValue).toLocaleString('en-US');
                      set_pay_price_total(formattedValue);
                    }}
                  />
                </Form.Group>


                <div className="d-flex flex-fill justify-content-between ">
                  <Button
                    className={`btn ${selectedPaymentMethod === 1 ? "btn-success" : "btn-light"} flex-grow-1 mr-1`}
                    onClick={() => {
                      setSelectedPaymentMethod(1);
                      noqr_code();
                    }}
                  >
                    ชำระด้วยเงินสด
                  </Button>
                  &nbsp;&nbsp;
                  <Button
                    className={`btn ${selectedPaymentMethod === 2 ? "btn-success" : "btn-light"} flex-grow-1 ml-1`}
                    onClick={() => {
                      setSelectedPaymentMethod(2);
                      qr_code();
                    }}
                  >
                    ชำระด้วย QR CODE
                  </Button>
                </div>
                {showqr && (
                  <div><center>
                    <img id="qrImage" src={pic_qr_code} alt="PromptPay QR Code" />
                    <p> {ownerName}</p>
                    <p>ยอดชำระ: {amount} บาท</p>
                  </center>
                  </div>
                )}


                <Modal.Footer>
                  <Button variant="success" type="submit" >
                    ยืนยัน
                  </Button>
                  <Button variant="danger" onClick={closeModal}>
                    ปิด
                  </Button>
                </Modal.Footer>
              </Form>
            </Modal.Body>
          </Modal>
        </main>
      </body>
    </>
  )

}

export default Dashboard
