import React, { useState, useEffect } from 'react'
import { Button, Modal, Form } from 'react-bootstrap';
import Navbar from "../Navbar";
import axios from 'axios';
import Swal from 'sweetalert';
const Customer = () => {
    const currentDate = new Date();
    const formattedDate = currentDate.toISOString().split('T')[0];

    const [showModal, setShowModal] = useState(false);
    const [showModaledit, setShowModaledit] = useState(false);

    const [users, setUsers] = useState([]);
    const [cus_id, setCus_id] = useState();
    const [cus_name, setCus_name] = useState("");
    const [cus_typeroom, setCus_typeroom] = useState("");
    const [cus_room, setCus_room] = useState("");
    const [cus_status, setCus_status] = useState(1);
    const [cus_datein, setCus_datein] = useState(formattedDate);
    
    const [edit_cus_id, set_edit_Cus_id] = useState();
    const [edit_cus_name, set_edit_Cus_name] = useState("");
    const [edit_cus_typeroom, set_edit_Cus_typeroom] = useState("");
    const [edit_cus_room, set_edit_Cus_room] = useState("");
    const [edit_cus_status, set_edit_Cus_status] = useState("");
    const [edit_cus_datein, set_edit_Cus_datein] = useState("");

    const [currentPage, setCurrentPage] = useState(1);
    const [totalPages, setTotalPages] = useState(1);
    const urlserver = "http://localhost:4000";
    //const urlserver = "https://nodejs-ktey.onrender.com";
    useEffect(() => {
        fetchUsers();

    }, []);


    const startIndex = (currentPage - 1) * 10;
    const endIndex = currentPage * 10;
    const currentUsers = users.slice(startIndex, endIndex);
    const changePage = (page) => {
        setCurrentPage(page);
    };
    const fetchUsers = async () => {
        try {
            const response = await axios.get(urlserver + `/api_customer`);
            setUsers(response.data);
            const num = response.data.length
            const cus_id_numlast = response.data[num - 1].cus_id
            setCus_id(cus_id_numlast + 1);
            setTotalPages(Math.ceil(response.data.length / 10));

        } catch (error) {
            console.error(error);
        }
    };
    const deleteRow = async (id) => {

        try {
            const response = await axios.delete(urlserver + `/api_customer/delete?cus_id=${id}`);
            console.log(response.data); // ตัวอย่างการใช้งาน response ที่ได้จาก server

            if (response.data !== "") {
                Swal({
                    title: '',
                    text: 'ลบข้อมูลสำเร็จ',
                    icon: 'success',
                });
                fetchUsers();
            }

        } catch (error) {
            console.error(error);
        }
    };
    const ob_status = {
        1: 'พักอาศัย',
        2: 'ออก',
    };

    const openModal = () => {
        setShowModal(true);

    };
    const closeModal = () => {
        setShowModal(false);
    };
    const openModaledit = (id) => {
        setShowModaledit(true);
        console.log(id)

    };
    const closeModaledit = () => {
        setShowModaledit(false);
    };
    const handleSignup = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post(urlserver + `/api_customer/create`, {
                cus_id,
                cus_name,
                cus_room,
                cus_typeroom,
                cus_status,
                cus_datein,
            });
            // กระบวนการอื่นๆ เมื่อสำเร็จ

            console.log(response.data); // ตัวอย่างการใช้งาน response ที่ได้จาก server
            if (response.data !== "") {
                Swal({
                    title: '',
                    text: 'บันทึกข้อมูลสำเร็จ',
                    icon: 'success',
                });
                setShowModal(false);
            }
            fetchUsers();
        } catch (error) {
            console.error(error);
        }
    };

    return (
        <>
   
                <Navbar />
                <main className="main-content position-relative border-radius-lg ">
                    {/* table */}
                    <br />
                    <div align="right" className=" container-fluid ">
                        <button type="button" className="btn btn-success" onClick={openModal} >
                            เพิ่มข้อมูล
                        </button>
                    </div>
                    <div className="container-fluid py2">
                        <div className="row">


                            <div className="col-12">
                                <div className="card mb-4">


                                    <div className="card-header pb-0">
                                        <h6>ข้อมูลลูกค้า</h6>
                                    </div>
                                    <div className="card-body px-0 pt-0 pb-2">
                                        <div className="table-responsive p-0">
                                            <table className="table align-items-center mb-0">
                                                <thead>
                                                    <tr>
                                                        <th className="text-center  text-secondary text-xs font-weight-bolder opacity-7">ชื่อ</th>
                                                        <th className="text-center  text-secondary text-xs font-weight-bolder opacity-7 ps-2">ห้องที่<br />ชนิดห้อง</th>
                                                        <th className="text-center  text-secondary text-xs font-weight-bolder opacity-7">สถานะ</th>
                                                        <th colSpan={2} className="text-center opacity-7">#</th>
                                                    </tr>
                                                </thead>
                                                <tbody>
                                                    {currentUsers.map((file, index) => (
                                                        <tr key={index}>
                                                            <td className="text-center  text-sm font-weight-bolder opacity-8">
                                                                {file.cus_name}
                                                            </td>
                                                            <td className="text-center text-sm font-weight-bolder opacity-8">
                                                                {file.cus_room + " " + file.cus_typeroom}
                                                            </td>
                                                            <td className="text-center ">
                                                                <span className="badge badge-sm bg-gradient-success">{ob_status[file.cus_status]}</span>
                                                            </td>
                                                            <td className="text-center">
                                                                <button className="btn btn-warning" type='button'  onClick={() => openModaledit(file.cus_id)}>
                                                                    <i className="fa-regular fa-pen-to-square"></i>
                                                                </button>
                                                                &nbsp;
                                                                <button className="btn btn-danger" type='button' onClick={() => deleteRow(file.cus_id)}>
                                                                    <i className="fa-solid fa-trash-can"></i>
                                                                </button>
                                                            </td>
                                                        </tr>
                                                    ))}
                                                </tbody>
                                            </table>
                                        </div>
                                    </div>


                                    <ul className="pagination justify-content-end">

                                        {Array.from({ length: totalPages }, (_, i) => i + 1).map((page) => (
                                            <li
                                                className={`page-item ${currentPage === page ? 'active' : ''}`}
                                                key={page}
                                                onClick={() => changePage(page)}
                                            >
                                                <span className="page-link">{page}</span>
                                            </li>
                                        ))}
                                    </ul>

                                </div>
                            </div>
                        </div>
                    </div>


                    <Modal show={showModal} onHide={closeModal}>
                        <Modal.Header closeButton>
                            <Modal.Title>เพิ่มข้อมูล</Modal.Title>
                        </Modal.Header>
                        <Modal.Body >
                            <Form onSubmit={handleSignup}>
                                <Form.Group hidden >
                                    <Form.Label>cus_id</Form.Label>
                                    <Form.Control
                                        type="text"
                                        value={cus_id}
                                        onChange={(e) => setCus_id(e.target.value)}
                                    />
                                </Form.Group>
                                <Form.Group>
                                    <Form.Label>ชื่อ-นามสกุล</Form.Label>
                                    <Form.Control
                                        type="text"
                                        value={cus_name}
                                        onChange={(e) => setCus_name(e.target.value)}
                                    />
                                </Form.Group>
                                <Form.Group>
                                    <Form.Label>ชนิดของห้อง</Form.Label>
                                    <Form.Control
                                        type="text"
                                        value={cus_typeroom}
                                        onChange={(e) => setCus_typeroom(e.target.value)}
                                    />
                                </Form.Group>
                                <Form.Group>
                                    <Form.Label>เลขห้อง</Form.Label>
                                    <Form.Control
                                        type="text"
                                        value={cus_room}
                                        onChange={(e) => setCus_room(e.target.value)}
                                    />
                                </Form.Group>
                                <Form.Group>
                                    <Form.Label>สถานะ</Form.Label>
                                    <Form.Control
                                        as="select"
                                        value={cus_status}
                                        onChange={(e) => setCus_status(e.target.value)}>
                                        <option value="">-- เลือกสถานะ --</option>
                                        <option value="1" >พักอาศัย</option>
                                        <option value="2">ออก</option>

                                    </Form.Control>
                                </Form.Group>
                                <Form.Group>
                                    <Form.Label>วันที่เข้าพัก</Form.Label>
                                    <Form.Control
                                        type="date"
                                        value={cus_datein}
                                        onChange={(e) => setCus_datein(e.target.value)}
                                    />
                                </Form.Group>
                                <Modal.Footer>
                                    <Button variant="primary" type="submit" >
                                        ยืนยัน
                                    </Button>
                                    <Button variant="secondary" onClick={closeModal}>
                                        ปิด
                                    </Button>
                                </Modal.Footer>
                            </Form>
                        </Modal.Body>
                    </Modal>

                    {/* <edit> */}
                    <Modal show={showModaledit} onHide={closeModal}>
                        <Modal.Header closeButton>
                            <Modal.Title>เพิ่มข้อมูล</Modal.Title>
                        </Modal.Header>
                        <Modal.Body >
                            <Form onSubmit={handleSignup}>
                                <Form.Group hidden >
                                    <Form.Label>cus_id</Form.Label>
                                    <Form.Control
                                        type="text"
                                        value={edit_cus_id}
                                        onChange={(e) => setCus_id(e.target.value)}
                                    />
                                </Form.Group>
                                <Form.Group>
                                    <Form.Label>ชื่อ-นามสกุล</Form.Label>
                                    <Form.Control
                                        type="text"
                                        value={edit_cus_name}
                                        onChange={(e) => setCus_name(e.target.value)}
                                    />
                                </Form.Group>
                                <Form.Group>
                                    <Form.Label>ชนิดของห้อง</Form.Label>
                                    <Form.Control
                                        type="text"
                                        value={edit_cus_typeroom}
                                        onChange={(e) => setCus_typeroom(e.target.value)}
                                    />
                                </Form.Group>
                                <Form.Group>
                                    <Form.Label>เลขห้อง</Form.Label>
                                    <Form.Control
                                        type="text"
                                        value={edit_cus_room}
                                        onChange={(e) => setCus_room(e.target.value)}
                                    />
                                </Form.Group>
                                <Form.Group>
                                    <Form.Label>สถานะ</Form.Label>
                                    <Form.Control
                                        as="select"
                                        value={edit_cus_status}
                                        onChange={(e) => setCus_status(e.target.value)}>
                                        <option value="">-- เลือกสถานะ --</option>
                                        <option value="1" >พักอาศัย</option>
                                        <option value="2">ออก</option>

                                    </Form.Control>
                                </Form.Group>
                                <Form.Group>
                                    <Form.Label>วันที่เข้าพัก</Form.Label>
                                    <Form.Control
                                        type="date"
                                        value={edit_cus_datein}
                                        onChange={(e) => setCus_datein(e.target.value)}
                                    />
                                </Form.Group>
                                <Modal.Footer>
                                    <Button variant="primary" type="submit" >
                                        ยืนยัน
                                    </Button>
                                    <Button variant="secondary" onClick={closeModaledit}>
                                        ปิด
                                    </Button>
                                </Modal.Footer>
                            </Form>
                        </Modal.Body>
                    </Modal>


                </main>
           
        </>
    )
};

export default Customer
