import React, { useState } from 'react';
import { Navigate } from 'react-router-dom';
import { Button, Modal, Form } from 'react-bootstrap';

function App() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    const handleLogin = () => {
        // ตรวจสอบเงื่อนไขการล็อกอิน
        if (email === '123' && password === '456') {
            setIsLoggedIn(true);
        } else {
            alert('Invalid email or password');
        }
    };

    if (isLoggedIn) {
        // หากล็อกอินสำเร็จแล้วให้เปลี่ยนเส้นทางไปหน้าหลังล็อกอิน (ตัวอย่างเป็นหน้า main_dashboard)
        return <Navigate to="/main_dashboard" />;
    }

    return (
        
        <div className="my-5" >
            
            <div className="card">
                
                <div className="row g-0">
                    <div className="col-md-3"></div>
                    <div className="col-md-6">
                        <div className="card-body d-flex flex-column">
                            <div className="d-flex flex-row mt-2">
                                <i className="fas fa-cubes fa-3x me-3" style={{ color: '#ff6219' }} />
                                <span className="h1 fw-bold mb-0">Logo</span>
                            </div>
                            <h5 className="fw-normal my-4 pb-3" style={{ letterSpacing: '1px' }}>Sign into your account</h5>
                            <input className="mb-4 form-control form-control-lg" placeholder="Email address" type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
                            <input className="mb-4 form-control form-control-lg" placeholder="Password" type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
                            <Button variant='dark' size='lg' onClick={handleLogin}>Login</Button>
                            <a className="small text-muted" href="#!">Forgot password?</a>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default App;
