import React, { useState } from 'react';
import { Navigate } from 'react-router-dom';
import { Button } from 'react-bootstrap';

function App() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    const handleLogin = () => {
        // ตรวจสอบเงื่อนไขการล็อกอิน
        if (email === '123' && password === '456') {
            setIsLoggedIn(true);
        } else {
            alert('Invalid email or password');
        }
    };

    if (isLoggedIn) {
        // หากล็อกอินสำเร็จแล้วให้เปลี่ยนเส้นทางไปหน้าหลังล็อกอิน (ตัวอย่างเป็นหน้า main_dashboard)
        return <Navigate to="/main_dashboard" />;
    }

    return (
        <>        <div className="my-5" style={{ backgroundColor: '#f5f5f5', minHeight: '100vh', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
            <div className="card" style={{ width: '600px',height:'500px' }}>
                <div className="card-body d-flex flex-column align-items-center">
                <img src="../assets/img/logo-ct-dark.png" width={75} height={75} alt="bin" />
                    <h5 className="fw-normal mb-4">Home Rental</h5>
                    <input className="mb-3 form-control" placeholder="Email address" type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
                    <input className="mb-3 form-control" placeholder="Password" type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
                    <br/>
                    <Button variant='dark' size='lg' block onClick={handleLogin}>Login</Button>
                    <a className="mt-3 small text-muted" href="#!">Forgot password?</a>
                </div>
            </div>
        </div>
        </>

    );
}

export default App;
