import React, { useState, useEffect } from 'react'
import Navbar from "../Navbar";
import axios from 'axios';
function Customer() {
    const [showModal, setShowModal] = useState(false);
    const [cus, setUsers] = useState([]);
    const urlserver = "http://localhost:4000";
    //const urlserver = "https://nodejs-ktey.onrender.com";
    useEffect(() => {
        fetchUsers();
    });

    const fetchUsers = async () => {
        try {
            const response = await axios.get(urlserver + `/api_customer`);
            setUsers(response.data);
            console.log(response.data);
        } catch (error) {
            console.error(error);
        }
    };
    const ob_status = {
        1: 'พักอาศัย',
        2: 'ออก',
    };
    const openModal = () => {
        setShowModal(true);
    };
    const closeModal = () => {
        setShowModal(false);
    };
    return (
        <>
            <body class="g-sidenav-show   bg-gray-100">
                <Navbar />
                <main class="main-content position-relative border-radius-lg ">
                    {/* table */}
                    <br />
                    <div align="right" class=" container-fluid ">
                        <button type="button" className="btn btn-success" onClick={openModal} >
                            เพิ่มข้อมูล
                        </button>
                    </div>
                    <div class="container-fluid py2">
                        <div class="row">


                            <div class="col-12">
                                <div class="card mb-4">


                                    <div class="card-header pb-0">
                                        <h6>ค่าเช่าที่ต้องเก็บ</h6>
                                    </div>
                                    <div class="card-body px-0 pt-0 pb-2">
                                        <div class="table-responsive p-0">
                                            <table class="table align-items-center mb-0">
                                                <thead>
                                                    <tr>
                                                        <th class="text-center  text-secondary text-xs font-weight-bolder opacity-7">ชื่อ</th>
                                                        <th class="text-center  text-secondary text-xs font-weight-bolder opacity-7 ps-2">ห้องที่/ชนิดห้อง</th>
                                                        <th class="text-center  text-secondary text-xs font-weight-bolder opacity-7">สถานะ</th>
                                                        <th class="text-center opacity-7">#</th>
                                                    </tr>
                                                </thead>
                                                <tbody>
                                                    {cus.map((file, index) => (
                                                        <tr key={index}>
                                                            <td class="text-center  text-sm font-weight-bolder opacity-8">
                                                                {file.cus_name}
                                                            </td>
                                                            <td class="text-center text-sm font-weight-bolder opacity-8">
                                                                {file.cus_room + " " + file.cus_typeroom}
                                                            </td>
                                                            <td class="text-center text-sm font-weight-bolder opacity-8">
                                                                <span class="badge badge-sm bg-gradient-success">{ob_status[file.cus_room]}</span>
                                                            </td>
                                                            <td class="text-center text-sm font-weight-bolder opacity-8"></td>
                                                        </tr>
                                                    ))}
                                                </tbody>

                                            </table>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                    <Modal show={showModal} onHide={closeModal}>
                        <Modal.Header closeButton>
                            <Modal.Title>เพิ่มข้อมูล</Modal.Title>
                        </Modal.Header>
                        <Modal.Body >
                            <Form onSubmit={handleSignup}>


                                <Form.Group>
                                    <Form.Label>Name</Form.Label>
                                    <Form.Control
                                        type="text"
                                        value={name}
                                        onChange={(e) => setName(e.target.value)}
                                    />
                                </Form.Group>
                                <Form.Group>
                                    <Form.Label>Surname</Form.Label>
                                    <Form.Control
                                        type="text"
                                        value={surname}
                                        onChange={(e) => setSurname(e.target.value)}
                                    />
                                </Form.Group>
                                <Form.Group>
                                    <Form.Label>email</Form.Label>
                                    <Form.Control
                                        type="text"
                                        value={username}
                                        onChange={(e) => setUsername(e.target.value)}
                                    />
                                </Form.Group>
                                <Form.Group>
                                    <Form.Label>Password</Form.Label>
                                    <Form.Control
                                        type="password"
                                        value={password}
                                        onChange={(e) => setPassword(e.target.value)}
                                    />
                                </Form.Group>
                                <Form.Group>
                                    <Form.Label>Phone</Form.Label>
                                    <Form.Control
                                        type="tel"
                                        value={phone}
                                        onChange={(e) => setPhone(e.target.value)}
                                    />
                                </Form.Group>
                                <Form.Group>
                                    <Form.Label>Address</Form.Label>
                                    <Form.Control
                                        as="textarea"
                                        rows={3}
                                        value={address}
                                        onChange={(e) => setAddress(e.target.value)}
                                    />
                                </Form.Group>
                                <Form.Group>
                                    <Form.Label>status</Form.Label>
                                    <Form.Control
                                        as="select"
                                        value={status}
                                        onChange={(e) => setstatus(e.target.value)}>
                                        <option value="">-- เลือกสถานะ --</option>
                                        <option value="1" >Active</option>
                                        <option value="2">Inactive</option>
                                        <option value="3" >Pending</option>
                                    </Form.Control>
                                </Form.Group>
                                <Form.Group>
                                    <Form.Label>รูปภาพ</Form.Label>  {imagePreview && <img src={imagePreview} alt="รูปภาพ" style={{ width: '50px' }} />}
                                    <Form.Control type="file" onChange={handleImageUpload} />

                                </Form.Group>
                                <Modal.Footer>
                                    <Button variant="primary" type="submit" >
                                        ยืนยัน
                                    </Button>
                                    <Button variant="secondary" onClick={closeModal}>
                                        ปิด
                                    </Button>
                                </Modal.Footer>
                            </Form>
                        </Modal.Body>
                    </Modal>
                </main>
            </body>
        </>
    )
}

export default Customer
